// components/admin/racks/SlotDetailModal.jsx
// ------------------------------------------------------
// Slot Detail + Add Item + Take Item Modal
// Works with items[] structure
// ------------------------------------------------------

import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const SlotDetailModal = ({
  show,
  onClose,
  slot,
  containerId,
  onAddItem,
}) => {
  const [showAddForm, setShowAddForm] = useState(false);
  const [showTakeModal, setShowTakeModal] = useState(false);

  if (!slot) return null;

  const items = slot.items || [];
  const item = items[0] || null;

  const [newItem, setNewItem] = useState({
    name: "",
    quantity: 1,
  });

  const [takeQty, setTakeQty] = useState(1);
  const [takeReturnable, setTakeReturnable] = useState(false);
  const [takeReturnDate, setTakeReturnDate] = useState("");

  useEffect(() => {
    if (!show) {
      setShowAddForm(false);
      setShowTakeModal(false);
      setNewItem({ name: "", quantity: 1 });
      setTakeQty(1);
    }
  }, [show]);

  // -------------------------------
  // Add Item Handler
  // -------------------------------
  const submitAddItem = () => {
    if (!newItem.name.trim()) return;

    const itemObj = {
      id: Date.now().toString(),
      name: newItem.name,
      quantity: Number(newItem.quantity),
      isReturnable: false,
      taken: [],
    };

    onAddItem(containerId, slot.slotNumber, itemObj);

    setShowAddForm(false);
    onClose();
  };

  // -------------------------------
  // TAKE ITEM HANDLER
  // -------------------------------
  const handleTakeItem = () => {
    const today = new Date().toISOString().split("T")[0];

    const record = {
      user: "Admin",
      qty: takeQty,
      date: today,
      returnDate: takeReturnable ? takeReturnDate : null,
    };

    item.taken.push(record);
    item.quantity -= takeQty;

    setShowTakeModal(false);
    onClose();
  };

  // Remaining Qty Calculation
  const remainingQty = (item) => {
    const used =
      item.taken?.reduce((sum, t) => sum + Number(t.qty), 0) || 0;
    return item.quantity - used;
  };

  return (
    <Modal show={show} onHide={onClose} centered size="lg">
      <Modal.Header closeButton>
        <Modal.Title>
          Slot {slot.slotNumber} — Details
        </Modal.Title>
      </Modal.Header>

      <Modal.Body
        style={{
          backgroundColor: "hsl(215, 25%, 12%)",
          color: "white",
        }}
      >
        {/* EMPTY SLOT */}
        {items.length === 0 && !showAddForm && (
          <p className="text-muted">This slot is empty.</p>
        )}

        {/* SHOW EXISTING ITEM */}
        {!showAddForm && item && (
          <div
            className="p-3 mb-3 rounded"
            style={{
              backgroundColor: "hsl(215, 25%, 16%)",
              border: "1px solid hsl(215, 20%, 25%)",
            }}
          >
            <div className="d-flex justify-content-between">
              <div>
                <strong>{item.name}</strong>
                <div className="small text-muted">
                  Quantity: {item.quantity}
                </div>
              </div>

              <div className="text-end small">
                {item.isReturnable ? (
                  <span className="text-info">Returnable</span>
                ) : (
                  <span className="text-warning">Non-returnable</span>
                )}
                <div>
                  Remaining: {remainingQty(item)}
                </div>
              </div>
            </div>

            {/* TAKEN HISTORY */}
            {item.taken?.length > 0 && (
              <div className="mt-3 small">
                <strong>Taken history:</strong>
                {item.taken.map((t, i) => (
                  <div key={i} className="text-muted">
                    • {t.user} — Qty {t.qty} — {t.date}{" "}
                    {t.returnDate
                      ? `→ Return: ${t.returnDate}`
                      : "(Not returned)"}
                  </div>
                ))}
              </div>
            )}
          </div>
        )}

        {/* ADD NEW ITEM FORM */}
        {showAddForm && (
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Item Name</Form.Label>
              <Form.Control
                value={newItem.name}
                onChange={(e) =>
                  setNewItem({ ...newItem, name: e.target.value })
                }
                placeholder="Enter item name"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                min="1"
                value={newItem.quantity}
                onChange={(e) =>
                  setNewItem({
                    ...newItem,
                    quantity: Number(e.target.value),
                  })
                }
              />
            </Form.Group>
          </Form>
        )}
      </Modal.Body>

      <Modal.Footer>
        {/* TAKE ITEM BUTTON */}
        {!showAddForm && item && (
          <Button
            variant="warning"
            onClick={() => setShowTakeModal(true)}
          >
            Take Item
          </Button>
        )}

        {/* ADD ITEM */}
        {!showAddForm && (
          <Button
            variant="primary"
            onClick={() => setShowAddForm(true)}
          >
            + Add Item
          </Button>
        )}

        {/* SAVE NEW ITEM */}
        {showAddForm && (
          <>
            <Button variant="secondary" onClick={() => setShowAddForm(false)}>
              Back
            </Button>

            <Button variant="primary" onClick={submitAddItem}>
              Save Item
            </Button>
          </>
        )}

        <Button variant="secondary" onClick={onClose}>
          Close
        </Button>
      </Modal.Footer>

      {/* TAKE ITEM MODAL */}
      <Modal show={showTakeModal} onHide={() => setShowTakeModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Take Item — {item?.name}</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p><strong>Available:</strong> {item?.quantity}</p>

          <Form.Group className="mb-3">
            <Form.Label>Qty to Take</Form.Label>
            <Form.Control
              type="number"
              min="1"
              max={item?.quantity}
              value={takeQty}
              onChange={(e) => setTakeQty(Number(e.target.value))}
            />
          </Form.Group>

          <Form.Check
            type="switch"
            label={takeReturnable ? "Returnable" : "Non-returnable"}
            checked={takeReturnable}
            onChange={() => setTakeReturnable(!takeReturnable)}
          />

          {takeReturnable && (
            <Form.Group className="mt-3">
              <Form.Label>Return Date</Form.Label>
              <Form.Control
                type="date"
                value={takeReturnDate}
                onChange={(e) => setTakeReturnDate(e.target.value)}
              />
            </Form.Group>
          )}
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowTakeModal(false)}>
            Cancel
          </Button>

          <Button variant="success" onClick={handleTakeItem}>
            Confirm
          </Button>
        </Modal.Footer>
      </Modal>
    </Modal>
  );
};

export default SlotDetailModal;
