import React from "react";
import { Package } from "lucide-react";

const ContainerSummaryCard = ({ container }) => {
  const occupied = container.slots.filter((s) => s.item !== null).length;
  const total = container.slots.length;
  const available = total - occupied;
  const occupancyRate = Math.round((occupied / total) * 100);

  return (
    <div
      className="card shadow-sm p-3"
      style={{
        background:
          "linear-gradient(90deg, hsl(215, 25%, 12%) 0%, hsl(215, 25%, 10%) 100%)",
        color: "hsl(210, 40%, 98%)",
        border: "1px solid hsl(215, 20%, 25%)",
        borderRadius: "10px",
      }}
    >
      {/* Header */}
      <div className="d-flex align-items-center gap-2 mb-2">
        <Package size={18} className="text-primary" />
        <h6 className="fw-bold mb-0">{container.name}</h6>
      </div>

      {/* Stats */}
      <div className="mb-2">
        <div className="d-flex justify-content-between">
          <span>Occupied</span>
          <span className="text-primary fw-semibold">
            {occupied}/{total}
          </span>
        </div>

        <div className="d-flex justify-content-between">
          <span>Available</span>
          <span className="text-success fw-semibold">{available}</span>
        </div>
      </div>

      {/* Progress Bar */}
      <div className="mt-2">
        <div
          className="progress"
          style={{
            height: "6px",
            backgroundColor: "hsl(215, 25%, 18%)",
            borderRadius: "4px",
          }}
        >
          <div
            className="progress-bar"
            role="progressbar"
            style={{
              width: `${occupancyRate}%`,
              backgroundColor: "hsl(210, 70%, 60%)",
            }}
          ></div>
        </div>

        <div className="text-center mt-2 text-muted small">
          {occupancyRate}% Capacity
        </div>
      </div>
    </div>
  );
};

export default ContainerSummaryCard;
