import React, { useEffect, useState } from "react";
import { Outlet } from "react-router-dom";
import UserNavbar from "../../components/users/UserNavbar";
import UserSidebar from "../../components/users/UserSidebar";
import { mockUsers } from "../../data/Mockdata";

const UserLayout = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 992);

  useEffect(() => {
    const onResize = () => setIsMobile(window.innerWidth < 992);
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  return (
    <div className="d-flex position-relative" style={{ minHeight: "100vh" }}>
      <UserSidebar isOpen={sidebarOpen} onClose={() => setSidebarOpen(!sidebarOpen)} />
      <div
        className="d-flex flex-column flex-grow-1"
        style={{
          marginLeft: isMobile ? 0 : sidebarOpen ? "240px" : "70px",
          transition: "margin-left 0.25s ease",
          width: "100%",
        }}
      >
        <UserNavbar onMenuClick={() => setSidebarOpen(!sidebarOpen)} />
        <main
          className="p-4"
          style={{
            background: "hsl(215,25%,14%)",
            color: "hsl(210,40%,98%)",
            minHeight: "100vh",
          }}
        >
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default UserLayout;
