import React, { useState, useContext } from "react";
import { AuthContext } from "../../context/AuthContext.jsx";
import { Form, Button, Container, Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import "./Auth.css";

const Register = () => {
  const { register } = useContext(AuthContext);
  const [form, setForm] = useState({ email: "", password: "", role: "user" });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    register(form.email, form.password, form.role);
  };

  return (
    <div className="auth-page">
      <Container fluid className="d-flex justify-content-center align-items-center vh-100">
        <Card className="auth-card p-4">
          <Card.Body>
            <h2 className="text-center auth-title">GANDEEVAN TECHNOLOGIES</h2>
            <h5 className="text-center text-light mb-3">Register</h5>

            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-3">
                <Form.Label>Email</Form.Label>
                <Form.Control
                  type="email"
                  name="email"
                  placeholder="Enter your email"
                  value={form.email}
                  onChange={handleChange}
                  required
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Password</Form.Label>
                <Form.Control
                  type="password"
                  name="password"
                  placeholder="Enter your password"
                  value={form.password}
                  onChange={handleChange}
                  required
                />
              </Form.Group>

              <Form.Group className="mb-4">
                <Form.Label className="emialpassword">Role</Form.Label>
                <Form.Select name="role" value={form.role} onChange={handleChange}>
                  <option value="manager">Manager</option>
                  <option value="user">User</option>
                </Form.Select>
              </Form.Group>

              <Button type="submit" className="w-100 auth-button">
                Register
              </Button>

              <div className="text-center mt-3">
                <Link to="/login" className="auth-link">
                  Already have an account? Login here
                </Link>
              </div>
            </Form>
          </Card.Body>
        </Card>
      </Container>
    </div>
  );
};

export default Register;
