import React from "react";

const LimitedStockItems = ({ lowStockItems = [], jumpToSlot }) => {
  // Fallback: if undefined, convert to empty array
  const items = Array.isArray(lowStockItems) ? lowStockItems : [];

  return (
    <div
      className="card shadow-sm"
      style={{
        backgroundColor: "hsl(215, 25%, 12%)",
        border: "1px solid hsl(215, 20%, 25%)",
        color: "white",
      }}
    >
      <div className="card-header fw-bold">Limited Stock Items</div>

      <div
        className="card-body"
        style={{
          maxHeight: "260px",
          overflowY: "auto",
        }}
      >
        {items.length === 0 ? (
          <p className="text-muted">No low-stock items</p>
        ) : (
          items.map((item) => (
            <div
              key={item.id}
              className="d-flex justify-content-between align-items-center p-3 mb-2 rounded"
              style={{
                backgroundColor: "hsl(215, 25%, 16%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              <div>
                <div className="fw-bold">{item.name}</div>
                <div className="small text-muted">
                  Remaining: {item.remaining}
                </div>
                <div className="small text-muted">
                  Rack {item.containerName} â€¢ Slot {item.slotNumber}
                </div>
              </div>

              <button
                className="btn btn-primary btn-sm"
                onClick={() =>
                  jumpToSlot(item.containerId, item.slotNumber)
                }
              >
                + Add
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LimitedStockItems;
