import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Mail, User, Calendar } from "lucide-react";

const PermissionRequests = ({ permissionRequests, onApprove, onReject }) => {
  return (
    <div
      className="card shadow-sm border-0"
      style={{
        background: "hsl(215, 25%, 12%)",
        color: "hsl(210, 40%, 98%)",
        border: "1px solid hsl(215, 20%, 25%)",
      }}
    >
      <div
        className="card-header"
        style={{
          background: "hsl(215, 25%, 10%)",
          borderBottom: "1px solid hsl(215, 20%, 25%)",
        }}
      >
        <h5 className="mb-0">Permission Requests</h5>
      </div>

      <div className="card-body">
        {permissionRequests.length === 0 ? (
          <p className="text-muted text-center">No permission requests</p>
        ) : (
          permissionRequests.map((req) => (
            <div
              key={req.id}
              className="p-3 mb-3 rounded"
              style={{
                background: "hsl(215, 25%, 14%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              <div className="d-flex justify-content-between flex-wrap">
                <div>
                  <h6 className="fw-bold mb-1">{req.itemName}</h6>
                  <p className="text-muted small mb-1">
                    Qty: {req.quantity} â€¢ {req.itemType}
                  </p>

                  <div className="d-flex align-items-center text-muted small gap-2">
                    <User size={14} />
                    {req.userName}

                    <Mail size={14} className="ms-3" />
                    {req.userEmail}

                    <Calendar size={14} className="ms-3" />
                    {req.dateRequested}
                  </div>

                  {req.returnDate && (
                    <p className="small text-info mt-1">
                      Return Date: {req.returnDate}
                    </p>
                  )}
                </div>

                <div className="d-flex gap-2 mt-2 mt-md-0">
                  <button
                    className="btn btn-success btn-sm"
                    onClick={() => onApprove(req.id)}
                  >
                    Approve
                  </button>

                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => onReject(req.id)}
                  >
                    Reject
                  </button>
                </div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default PermissionRequests;
