import axios from "axios";

const api = axios.create({
  baseURL: "http://localhost:8000/api", // VERIFY: Ensure this is your correct backend API base URL
});

api.interceptors.request.use((config) => {
  let token = localStorage.getItem("token");

  if (token) {
    // FIX: Remove accidental surrounding quotes from the token string
    if (token.startsWith('"') && token.endsWith('"')) {
      token = token.slice(1, -1);
    }

    // Attach the token in the required 'Bearer <token>' format
    config.headers.Authorization = `Bearer ${token}`;
  }
  
  return config;
});

export default api;
