// Slot Detail + Add Item Modal + Return Item
import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import { Trash2 } from "lucide-react";
import api from "../../../api/axios";
import NotificationToast from "../../common/NotificationToast";
import AddItemModal from "./AddItemModal"; // ← STEP 1: Import the new component

const SlotDetailModal = ({
  show,
  onClose,
  slot,
  containerId,
  onAddItem,
  refresh,
}) => {
  // ← STEP 2: Change showAddForm to showAddItemModal
  const [showAddItemModal, setShowAddItemModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editItem, setEditItem] = useState(null);
  const [showDeleteModal, setShowDeleteModal] = useState(false);
  const [deleteItem, setDeleteItem] = useState(null);

  // ← STEP 3: Remove the old newItem state (we don't need it anymore)
  // const [newItem, setNewItem] = useState({
  //   name: "",
  //   quantity: 1,
  // });

  const [showReturnModal, setShowReturnModal] = useState(false);
  const [returnContext, setReturnContext] = useState(null);
  const [returnQty, setReturnQty] = useState(1);
  const [selectedBorrower, setSelectedBorrower] = useState("");
  const [toast, setToast] = useState({
    show: false,
    message: "",
    bg: "success",
  });

  useEffect(() => {
    console.log("SLOT RECEIVED (frontend):", slot);
  }, [slot]);

  useEffect(() => {
    if (!show) {
      setShowAddItemModal(false); // ← Updated
      setShowReturnModal(false);
      setReturnContext(null);
      setReturnQty(1);
      setSelectedBorrower("");
    }
  }, [show]);

  useEffect(() => {
    console.log("SLOT LOADED:", slot);
  }, [slot]);

  if (!slot) return null;

  const items =
    Array.isArray(slot.items) && slot.items.length > 0
      ? slot.items
      : slot.item
        ? [
            {
              id: slot.item.itemId,
              name: slot.item.itemName,
              quantity: slot.item.total,
              remaining: slot.item.remaining,
              takenHistory: slot.item.takenHistory || [],
            },
          ]
        : [];

  const remainingQty = (it) =>
    Number(
      it.remaining !== undefined && it.remaining !== null
        ? it.remaining
        : it.quantity !== undefined
          ? it.quantity
          : it.total !== undefined
            ? it.total
            : 0
    );

  const totalQty = (it) =>
    Number(
      it.quantity !== undefined
        ? it.quantity
        : it.total !== undefined
          ? it.total
          : remainingQty(it)
    );

  // ← STEP 4: New function to handle data from AddItemModal
  const handleAddItemSubmit = (itemData) => {
    if (!slot.slotId) return;

    // Send the complete item data to backend
    onAddItem(containerId, slot.slotId, itemData);
    
    refresh();
    setShowAddItemModal(false);
    onClose();
    
    setToast({
      show: true,
      message: "Item added successfully!",
      bg: "success",
    });
  };

  // ← STEP 5: Keep all your existing functions (no changes needed)
  const openReturnModal = (item) => {
    const itemHistory = item.takenHistory || [];

    if (itemHistory.length === 0) {
      setToast({
        show: true,
        message: "No borrow history found for this item.",
        bg: "warning",
      });
      return;
    }

    setReturnContext({
      item,
      historyList: itemHistory,
    });

    setShowReturnModal(true);
  };

  const openEditModal = (item) => {
    setEditItem({
      ...item,
      name: item.name || item.itemName,
      quantity: totalQty(item),
      itemCategory: item.itemCategory || "",
      componentType: item.componentType || "",
      package: item.package || "",
      partNumber: item.partNumber || "",
      manufacturer: item.manufacturer || "",
      value: item.value || "",
      tolerance: item.tolerance || "",
      voltageRating: item.voltageRating || "",
      currentRating: item.currentRating || "",
      displayType: item.displayType || "",
      displayInterface: item.displayInterface || "",
      displayColor: item.displayColor || "",
      protectionType: item.protectionType || "",
      protectionLocation: item.protectionLocation || "",
      moduleType: item.moduleType || "",
    });
    setShowEditModal(true);
  };

  const handleDeleteItem = async () => {
    if (!deleteItem) return;

    try {
      await api.delete(
        `/racks/deleteItem/${containerId}/${slot.slotId}/${deleteItem.itemId}`
      );

      setToast({
        show: true,
        message: "Item deleted successfully",
        bg: "success",
      });

      setShowDeleteModal(false);
      setDeleteItem(null);
      setShowEditModal(false);
      setEditItem(null);

      refresh();
      onClose();
    } catch (err) {
      console.error("Delete item error:", err);
      setToast({
        show: true,
        message: "Failed to delete item",
        bg: "danger",
      });
    }
  };

  const openDeleteModal = (item) => {
    setDeleteItem(item);
    setShowDeleteModal(true);
  };

  const handleEditSave = async () => {
    if (!editItem) return;

    try {
      await api.put(
        `/racks/updateItem/${containerId}/${slot.slotId}/${editItem.itemId}`,
        {
          itemName: editItem.name,
          total: editItem.quantity,
          itemCategory: editItem.itemCategory,
          componentType: editItem.componentType,
          package: editItem.package,
          partNumber: editItem.partNumber,
          manufacturer: editItem.manufacturer,
          value: editItem.value,
          tolerance: editItem.tolerance,
          voltageRating: editItem.voltageRating,
          currentRating: editItem.currentRating,
          displayType: editItem.displayType,
          displayInterface: editItem.displayInterface,
          displayColor: editItem.displayColor,
          protectionType: editItem.protectionType,
          protectionLocation: editItem.protectionLocation,
          moduleType: editItem.moduleType,
        }
      );

      setShowDeleteModal(false);
      setDeleteItem(null);
      setShowEditModal(false);
      setEditItem(null);
      refresh();
      onClose();

      setToast({
        show: true,
        message: "Item updated successfully",
        bg: "success",
      });
    } catch (err) {
      console.error("Edit item error:", err);
      setToast({
        show: true,
        message: err.response?.data?.message || "Update failed",
        bg: "danger",
      });
    }
  };

  const confirmReturn = async () => {
    if (!returnContext) return;

    const borrower = returnContext.historyList.find(
      (h) => h.borrowId === selectedBorrower
    );

    if (!borrower) {
      setToast({
        show: true,
        message: "Select a borrower first.",
        bg: "warning",
      });
      return;
    }

    const qty = Number(returnQty);

    if (qty < 1 || qty > borrower.quantity) {
      setToast({
        show: true,
        message: `Enter valid quantity (1 .. ${borrower.quantity})`,
        bg: "danger",
      });
      return;
    }

    const payload = {
      rackId: slot.rackId,
      rackName: slot.rackName,
      slotNumber: slot.slotNumber,
      returnQuantity: qty,
    };
    console.log("SENDING RETURN PAYLOAD:", payload);

    try {
      await api.post(`/manager/returnItem/${borrower.borrowId}`, {
        rackId: slot.rackId,
        rackName: slot.rackName,
        slotNumber: slot.slotNumber,
        returnQuantity: qty,
      });

      setToast({
        show: true,
        message: "Item returned successfully!",
        bg: "success",
      });
      refresh();
      setShowReturnModal(false);
      setReturnContext(null);
      setReturnQty(1);
      setSelectedBorrower("");
      onClose();
    } catch (err) {
      console.error("Return failed:", err);
      setToast({
        show: true,
        message: "Failed to return item.",
        bg: "danger",
      });
    }
  };

  return (
    <>
      <NotificationToast
        show={toast.show}
        message={toast.message}
        bg={toast.bg}
        onClose={() => setToast((prev) => ({ ...prev, show: false }))}
      />

      {/* MAIN MODAL - Slot Details */}
      <Modal show={show} onHide={onClose} centered size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            Slot {slot.slotNumber}{" "}
            <strong className="text-muted">-Details</strong>
          </Modal.Title>
        </Modal.Header>

        <Modal.Body
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "white",
          }}
        >
          {/* ← STEP 6: Remove showAddForm check - we don't need inline form anymore */}
          {items.length === 0 && (
            <p className="text-muted">This slot is empty.</p>
          )}

          {/* Display all items in the slot */}
          {items.map((it) => (
            <div
              key={it.id || it.itemId}
              className="p-3 mb-3 rounded"
              style={{
                backgroundColor: "hsl(215, 25%, 16%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              <div className="d-flex justify-content-between">
                <div>
                  <strong>{it.name || it.itemName}</strong>
                  <div className="small text-muted">
                    <span>Total: {totalQty(it)}</span>
                    {it.itemCategory && (
                      <span className="ms-3">Category: {it.itemCategory}</span>
                    )}
                    {it.componentType && (
                      <span className="ms-3">Type: {it.componentType}</span>
                    )}
                    {it.package && (
                      <span className="ms-3">Package: {it.package}</span>
                    )}
                    {it.value && (
                      <span className="ms-3">Value: {it.value}</span>
                    )}
                    {it.tolerance && (
                      <span className="ms-3">Tolerance: {it.tolerance}</span>
                    )}
                    {it.displayType && (
                      <span className="ms-3">Display: {it.displayType}</span>
                    )}
                    {it.displayInterface && (
                      <span className="ms-3">Interface: {it.displayInterface}</span>
                    )}
                    {it.protectionType && (
                      <span className="ms-3">Protection: {it.protectionType}</span>
                    )}
                    {it.moduleType && (
                      <span className="ms-3">Module: {it.moduleType}</span>
                    )}
                  </div>
                </div>

                <div className="text-end small">
                  <div>Remaining: {remainingQty(it)}</div>
                </div>
              </div>

              {it.takenHistory?.length > 0 && (
                <div className="mt-3 small">
                  <strong>Taken History:</strong>

                  {[...it.takenHistory]
                    .slice(-10)
                    .reverse()
                    .map((t, idx) => (
                      <div key={idx} className="text-muted">
                        • {t.userName} — Qty {t.quantity} —{" "}
                        {new Date(t.takenDate).toLocaleDateString("en-GB")}{" "}
                        {t.returnDate
                          ? `→ Return: ${new Date(
                              t.returnDate
                            ).toLocaleDateString("en-GB")}`
                          : "(Not returned)"}
                      </div>
                    ))}
                </div>
              )}

              <div className="d-flex justify-content-end mt-2 gap-2">
                <Button
                  size="sm"
                  variant="outline-primary"
                  onClick={() => openEditModal(it)}
                >
                  Edit
                </Button>

                <Button
                  size="sm"
                  variant="outline-danger"
                  onClick={() => openDeleteModal(it)}
                >
                  <Trash2 size={16} />
                </Button>

                {remainingQty(it) < totalQty(it) && (
                  <Button
                    size="sm"
                    variant="warning"
                    onClick={() => openReturnModal(it)}
                  >
                    Return
                  </Button>
                )}
              </div>
            </div>
          ))}

          {/* ← STEP 7: Remove the old inline form - it's deleted! */}
        </Modal.Body>

        <Modal.Footer>
          {/* ← STEP 8: Update button to open AddItemModal */}
          <Button
            variant="primary"
            onClick={() => setShowAddItemModal(true)}
          >
            + Add Item
          </Button>
        </Modal.Footer>
      </Modal>

      {/* ← STEP 9: Add the new AddItemModal component */}
      <AddItemModal
        show={showAddItemModal}
        onClose={() => setShowAddItemModal(false)}
        onSubmit={handleAddItemSubmit}
        slotNumber={slot.slotNumber}
      />

      {/* RETURN MODAL */}
      <Modal
        show={showReturnModal}
        onHide={() => setShowReturnModal(false)}
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Return Item</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          {returnContext && (
            <>
              <p>
                <strong>Item:</strong>{" "}
                {returnContext.item.itemName || returnContext.item.name}
              </p>

              <Form.Group className="mb-3">
                <Form.Label>Select Borrower</Form.Label>
                <Form.Control
                  as="select"
                  value={selectedBorrower}
                  onChange={(e) => {
                    setSelectedBorrower(e.target.value);

                    const userRec = returnContext.historyList.find(
                      (r) => r.borrowId === e.target.value
                    );
                    if (userRec) setReturnQty(userRec.quantity);
                  }}
                >
                  <option value="">-- Select User --</option>
                  {returnContext.historyList.map((h, idx) => (
                    <option key={idx} value={h.borrowId}>
                      {h.userName} (Borrowed: {h.quantity})
                    </option>
                  ))}
                </Form.Control>
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Quantity to Return</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={returnQty}
                  onChange={(e) => setReturnQty(Number(e.target.value))}
                />
              </Form.Group>
            </>
          )}
        </Modal.Body>

        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowReturnModal(false)}
          >
            Cancel
          </Button>
          <Button variant="success" onClick={confirmReturn}>
            Confirm Return
          </Button>
        </Modal.Footer>
      </Modal>

      {/* EDIT ITEM MODAL */}
      <Modal show={showEditModal} onHide={() => setShowEditModal(false)} centered size="lg" scrollable>
        <Modal.Header closeButton>
          <Modal.Title>Edit Item</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          {editItem && (
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Item Name</Form.Label>
                <Form.Control
                  value={editItem.name}
                  onChange={(e) =>
                    setEditItem({ ...editItem, name: e.target.value })
                  }
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Total Quantity</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={editItem.quantity}
                  onChange={(e) =>
                    setEditItem({
                      ...editItem,
                      quantity: Number(e.target.value),
                    })
                  }
                />
              </Form.Group>

              {/* Component Details */}
              {editItem.itemCategory && editItem.itemCategory !== "Others" && (
                <>
                  <hr />
                  <h6 className="mb-3">Component Details</h6>

                  {editItem.itemCategory && (
                    <Form.Group className="mb-3">
                      <Form.Label>Item Category</Form.Label>
                      <Form.Control
                        value={editItem.itemCategory}
                        disabled
                        readOnly
                      />
                    </Form.Group>
                  )}

                  {editItem.componentType && (
                    <Form.Group className="mb-3">
                      <Form.Label>Component Type</Form.Label>
                      <Form.Control
                        value={editItem.componentType}
                        onChange={(e) =>
                          setEditItem({ ...editItem, componentType: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.moduleType && (
                    <Form.Group className="mb-3">
                      <Form.Label>Module Type</Form.Label>
                      <Form.Control
                        value={editItem.moduleType}
                        onChange={(e) =>
                          setEditItem({ ...editItem, moduleType: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.package && (
                    <Form.Group className="mb-3">
                      <Form.Label>Package</Form.Label>
                      <Form.Control
                        value={editItem.package}
                        onChange={(e) =>
                          setEditItem({ ...editItem, package: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {(editItem.partNumber !== undefined) && (
                    <Form.Group className="mb-3">
                      <Form.Label>Part Number</Form.Label>
                      <Form.Control
                        value={editItem.partNumber}
                        onChange={(e) =>
                          setEditItem({ ...editItem, partNumber: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {(editItem.manufacturer !== undefined) && (
                    <Form.Group className="mb-3">
                      <Form.Label>Manufacturer</Form.Label>
                      <Form.Control
                        value={editItem.manufacturer}
                        onChange={(e) =>
                          setEditItem({ ...editItem, manufacturer: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.value !== undefined && (
                    <Form.Group className="mb-3">
                      <Form.Label>Value</Form.Label>
                      <Form.Control
                        value={editItem.value}
                        onChange={(e) =>
                          setEditItem({ ...editItem, value: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.tolerance !== undefined && (
                    <Form.Group className="mb-3">
                      <Form.Label>Tolerance</Form.Label>
                      <Form.Control
                        value={editItem.tolerance}
                        onChange={(e) =>
                          setEditItem({ ...editItem, tolerance: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.voltageRating !== undefined && (
                    <Form.Group className="mb-3">
                      <Form.Label>Voltage Rating</Form.Label>
                      <Form.Control
                        value={editItem.voltageRating}
                        onChange={(e) =>
                          setEditItem({ ...editItem, voltageRating: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.currentRating !== undefined && (
                    <Form.Group className="mb-3">
                      <Form.Label>Current Rating</Form.Label>
                      <Form.Control
                        value={editItem.currentRating}
                        onChange={(e) =>
                          setEditItem({ ...editItem, currentRating: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.displayType && (
                    <Form.Group className="mb-3">
                      <Form.Label>Display Type</Form.Label>
                      <Form.Control
                        value={editItem.displayType}
                        onChange={(e) =>
                          setEditItem({ ...editItem, displayType: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.displayInterface && (
                    <Form.Group className="mb-3">
                      <Form.Label>Display Interface</Form.Label>
                      <Form.Control
                        value={editItem.displayInterface}
                        onChange={(e) =>
                          setEditItem({ ...editItem, displayInterface: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.displayColor && (
                    <Form.Group className="mb-3">
                      <Form.Label>Display Color</Form.Label>
                      <Form.Control
                        value={editItem.displayColor}
                        onChange={(e) =>
                          setEditItem({ ...editItem, displayColor: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.protectionType && (
                    <Form.Group className="mb-3">
                      <Form.Label>Protection Type</Form.Label>
                      <Form.Control
                        value={editItem.protectionType}
                        onChange={(e) =>
                          setEditItem({ ...editItem, protectionType: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}

                  {editItem.protectionLocation && (
                    <Form.Group className="mb-3">
                      <Form.Label>Protection Location</Form.Label>
                      <Form.Control
                        value={editItem.protectionLocation}
                        onChange={(e) =>
                          setEditItem({ ...editItem, protectionLocation: e.target.value })
                        }
                      />
                    </Form.Group>
                  )}
                </>
              )}
            </Form>
          )}
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditModal(false)}>
            Cancel
          </Button>

          <Button type="button" variant="primary" onClick={handleEditSave}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>

      {/* DELETE CONFIRMATION MODAL */}
      <Modal
        show={showDeleteModal}
        onHide={() => setShowDeleteModal(false)}
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Confirm Delete</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <p>
            Are you sure you want to delete{" "}
            <strong>{deleteItem?.name || deleteItem?.itemName}</strong>?
          </p>
        </Modal.Body>

        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowDeleteModal(false)}
          >
            Cancel
          </Button>

          <Button variant="danger" onClick={handleDeleteItem}>
            Yes, Delete
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default SlotDetailModal;
