import React, { useState } from "react";
import ContainerGrid from "../components/admin/ContainerGrid";
import { mockContainers } from "../data/Mockdata";
import SearchBar from "../components/admin/SearchBar";

const Containers = () => {
  const [searchResult, setSearchResult] = useState("");

  return (
    <div
      className="container-fluid my-4 px-3 px-md-4"
      style={{
        color: "hsl(210, 40%, 98%)",
      }}
    >
      {/* Page Header */}
      <div className="mb-4 text-center text-md-start">
        <h2 className="fw-bold mb-1">Container Management</h2>
        <p className="text-muted">
          Manage all your containers and slots efficiently
        </p>
      </div>

      {/* Search Section */}
      <div className="mb-4">
        <SearchBar
          containers={mockContainers}
          onSearchResult={setSearchResult}
        />
        {searchResult && (
          <div
            className="alert alert-info mt-3 text-center text-md-start"
            role="alert"
          >
            {searchResult}
          </div>
        )}
      </div>

      {/* Container List (Responsive Grid) */}
      <div className="row g-4">
        {mockContainers.map((container) => (
          <div
            key={container.id}
            className="col-12 col-sm-6 col-lg-4 d-flex"
            style={{ minHeight: "100%" }}
          >
            <ContainerGrid container={container} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default Containers;
