import React, { useState, useEffect } from "react";
import { Modal, Button, Form, Row, Col } from "react-bootstrap";

/* ------------------ EMPTY STATE ------------------ */
const emptyState = {
  name: "",
  quantity: 1,
  itemCategory: "",
  componentType: "",
  componentTypeOther: "",
  package: "",
  packageOther: "",
  partNumber: "",
  manufacturer: "",
  value: "",
  tolerance: "",
  voltageRating: "",
  currentRating: "",
  displayType: "",
  displayTypeOther: "",
  displayInterface: "",
  displayInterfaceOther: "",
  displayColor: "",
  displayColorOther: "",
  protectionType: "",
  protectionTypeOther: "",
  protectionLocation: "",
  protectionLocationOther: "",
  moduleType: "",
  moduleTypeOther: "",
};

const AddItemModal = ({
  show,
  onClose,
  onSubmit,
  slotNumber,
  mode = "add",
  initialData = null,
}) => {
  const [data, setData] = useState(emptyState);

  /* ------------------ LOAD EDIT DATA ------------------ */
  useEffect(() => {
    if (mode === "edit" && initialData && show) {
      setData({
        ...emptyState,
        name: initialData.itemName || initialData.name || "",
        quantity: initialData.total || initialData.quantity || 1,
        itemCategory: initialData.itemCategory || "",
        componentType: initialData.componentType || "",
        package: initialData.package || "",
        partNumber: initialData.partNumber || "",
        manufacturer: initialData.manufacturer || "",
        value: initialData.value || "",
        tolerance: initialData.tolerance || "",
        voltageRating: initialData.voltageRating || "",
        currentRating: initialData.currentRating || "",
        displayType: initialData.displayType || "",
        displayInterface: initialData.displayInterface || "",
        displayColor: initialData.displayColor || "",
        protectionType: initialData.protectionType || "",
        protectionLocation: initialData.protectionLocation || "",
        moduleType: initialData.moduleType || "",
      });
    }

    if (mode === "add" && show) {
      setData(emptyState);
    }
  }, [mode, initialData, show]);

  /* ------------------ OPTIONS ------------------ */
  const categories = [
    "Passive Component",
    "Discrete Component",
    "Power Component",
    "MCU / Processor",
    "Memory IC",
    "Interface / Support IC",
    "Analog IC",
    "Sensor",
    "Connector",
    "Switch / Button",
    "Module",
    "Display",
    "Protection Component",
    "Mechanical",
    "Cable / Wire",
    "Tool / Accessory",
    "Others",
  ];

  const componentTypeOptions = {
    "Passive Component": [
      "Resistor",
      "Capacitor",
      "Inductor",
      "Ferrite Bead",
      "Thermistor",
      "Varistor",
      "Crystal / Resonator",
    ],
    "Discrete Component": [
      "Diode",
      "Zener Diode",
      "LED",
      "Transistor BJT",
      "MOSFET",
      "IGBT",
    ],
    "Power Component": [
      "LDO Regulator",
      "Buck Converter",
      "Boost Converter",
      "Buck-Boost Converter",
      "PMIC",
      "DC-DC Module",
      "Battery Charger",
    ],
    "MCU / Processor": ["Microcontroller", "Microprocessor", "SoC"],
    "Interface / Support IC": [
      "CAN Transceiver",
      "USB Interface",
      "RS485 Transceiver",
      "Ethernet PHY",
      "GPIO Expander",
      "RTC",
      "Watchdog",
      "UART Transceiver",
    ],
    "Analog IC": [
      "Operational Amplifier",
      "Comparator",
      "Instrumentation Amplifier",
      "Audio Amplifier",
      "ADC",
      "DAC",
      "Voltage Reference",
    ],
    Sensor: [
      "Temperature",
      "Pressure",
      "Accelerometer",
      "Gyroscope",
      "Proximity",
      "Gas",
    ],
    Connector: ["Board-to-Wire", "Board-to-Board", "Wire-to-Wire"],
    "Switch / Button": [
      "Tactile Switch",
      "Toggle Switch",
      "Push Button",
      "Slide Switch",
    ],
    "Memory IC": ["SRAM", "SDRAM", "SPI Flash", "EEPROM"],
  };

  const moduleTypes = [
    "Wireless Module",
    "Power Module",
    "Sensor Module",
    "Communication Module",
    "Display Module",
  ];

  const packageOptions = [
    "0201",
    "0402",
    "0603",
    "0805",
    "1206",
    "SOT-23",
    "SOIC-8",
    "QFN-32",
    "LQFP-64",
  ];

  const displayTypes = ["LCD", "OLED", "TFT LCD"];
  const displayInterfaces = ["SPI", "I2C", "Parallel"];
  const displayColors = ["Monochrome", "RGB"];

  const protectionTypes = ["TVS Diode", "ESD Diode", "Fuse"];
  const protectionLocations = ["Power Line", "Signal Line"];

  /* ------------------ FLAGS ------------------ */
  const isDisplay = data.itemCategory === "Display";
  const isProtection = data.itemCategory === "Protection Component";
  const isModule = data.itemCategory === "Module";

  /* ------------------ SUBMIT ------------------ */
  const handleSubmit = () => {
    if (!data.name.trim()) return alert("Enter Item Name");
    if (!data.itemCategory) return alert("Select Category");
    if (data.quantity < 1) return alert("Quantity must be >= 1");

    const submitData = {
      name: data.name,
      quantity: data.quantity,
      itemCategory: data.itemCategory,
      componentType:
        data.componentType === "Other"
          ? data.componentTypeOther
          : data.componentType,
      package: data.package === "Other" ? data.packageOther : data.package,
      partNumber: data.partNumber,
      manufacturer: data.manufacturer,
      value: data.value,
      tolerance: data.tolerance,
      voltageRating: data.voltageRating,
      currentRating: data.currentRating,
      displayType: data.displayType,
      displayInterface: data.displayInterface,
      displayColor: data.displayColor,
      protectionType: data.protectionType,
      protectionLocation: data.protectionLocation,
      moduleType: data.moduleType,
    };

    onSubmit(submitData);
    setData(emptyState);
    onClose();
  };

  /* ------------------ UI ------------------ */
  return (
    <Modal show={show} onHide={onClose} centered size="lg" scrollable>
      <Modal.Header closeButton>
        <Modal.Title>
          Slot {slotNumber} â€“ {mode === "edit" ? "Edit Item" : "Add Item"}
        </Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form>
          {/* BASIC */}
          <Row>
            <Col md={6}>
              <Form.Control
                placeholder="Item Name"
                value={data.name}
                onChange={(e) =>
                  setData({ ...data, name: e.target.value })
                }
              />
            </Col>

            <Col md={6}>
              <Form.Select
                value={data.itemCategory}
                onChange={(e) =>
                  setData({
                    ...data,
                    itemCategory: e.target.value,
                    componentType: "",
                  })
                }
              >
                <option value="">Select Category</option>
                {categories.map((c) => (
                  <option key={c}>{c}</option>
                ))}
              </Form.Select>
            </Col>
          </Row>

          <br />

          {/* COMPONENT TYPE (FIXED) */}
          {componentTypeOptions[data.itemCategory] && (
            <Form.Select
              value={data.componentType}
              onChange={(e) =>
                setData({ ...data, componentType: e.target.value })
              }
            >
              <option value="">Select Component Type</option>
              {componentTypeOptions[data.itemCategory].map((t) => (
                <option key={t}>{t}</option>
              ))}
              <option value="Other">Other</option>
            </Form.Select>
          )}

          <br />

          {/* MODULE */}
          {isModule && (
            <Form.Select
              value={data.moduleType}
              onChange={(e) =>
                setData({ ...data, moduleType: e.target.value })
              }
            >
              <option value="">Select Module Type</option>
              {moduleTypes.map((m) => (
                <option key={m}>{m}</option>
              ))}
            </Form.Select>
          )}

          <br />

          {/* PACKAGE */}
          {!isDisplay && !isProtection && (
            <Form.Select
              value={data.package}
              onChange={(e) =>
                setData({ ...data, package: e.target.value })
              }
            >
              <option value="">Select Package</option>
              {packageOptions.map((p) => (
                <option key={p}>{p}</option>
              ))}
            </Form.Select>
          )}
        </Form>
      </Modal.Body>

      <Modal.Footer>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleSubmit}>Save</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AddItemModal;
