// components/admin/ContainerGrid.jsx
import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import SlotCard from "./SlotCard";
import { Modal, Button, Form } from "react-bootstrap";
import { Trash2 } from "lucide-react";

/**
 * Props:
 *  - container
 *  - activeFilter
 *  - onAddSlot(containerId)
 *  - onDeleteRack(containerId)
 *  - onDeleteSlot(containerId, slotNumber)
 *  - onAddItem(containerId, slotNumber, newItem)   <-- IMPORTANT: parent must supply this
 *  - onExternalSlotClick(containerId, slotNumber)
 */
const ContainerGrid = ({
  container,
  activeFilter = "All",
  onAddSlot,
  onDeleteRack,
  onDeleteSlot,
  onAddItem,
  onExternalSlotClick,
}) => {
  const occupiedSlots = container.slots.filter((s) => s.items && s.items.length > 0).length;
  const totalSlots = container.slots.length;

  const [selectedSlot, setSelectedSlot] = useState(null);
  const [isDetailModalOpen, setIsDetailModalOpen] = useState(false);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  // newItem holds the form values for adding an item (and optional taken info)
  const [newItem, setNewItem] = useState({
    id: "",
    name: "",
    quantity: 1,
    isReturnable: true,
    // taken info (optional)
    takenBy: "",
    takenQty: 0,
    takenDate: "",
    returnDate: "",
  });

  const handleSlotClick = (slotNumber) => {
    const slot = container.slots.find((s) => s.slotNumber === slotNumber);
    setSelectedSlot(slotNumber);

    if (slot && slot.items && slot.items.length > 0) {
      // occupied -> show details
      setIsDetailModalOpen(true);
    } else {
      // empty -> open add modal
      setIsAddModalOpen(true);
    }

    if (onExternalSlotClick) onExternalSlotClick(container.id, slotNumber);
  };

  // called when the + button in SlotCard is clicked
  const handleAddButtonFromCard = (cId, sNumber) => {
    setSelectedSlot(sNumber);
    setIsAddModalOpen(true);
  };

  const selectedSlotData = container.slots.find((s) => s.slotNumber === selectedSlot) || { items: [] };

  // compute remaining qty for each item = item.quantity - sum(taken.qty)
  const remainingFor = (item) => {
    const takenSum = (item.taken || []).reduce((acc, t) => acc + (t.qty || 0), 0);
    return (item.quantity || 0) - takenSum;
  };

  const matchesFilter = (slot) => {
    const item = (slot.items && slot.items[0]) || null;
    switch (activeFilter) {
      case "All":
        return true;
      case "Returnable":
        return item && item.isReturnable === true;
      case "Non-returnable":
        return item && item.isReturnable === false;
      case "Occupied":
        return !!item;
      case "Empty":
        return !item;
      default:
        return true;
    }
  };

  // Add item form submit
  const submitAddItem = () => {
    // build item object to append into slot.items
    const itemId = newItem.id || Date.now().toString();
    const itemObj = {
      id: itemId,
      name: newItem.name.trim(),
      quantity: Number(newItem.quantity) || 1,
      isReturnable: !!newItem.isReturnable,
      taken: [],
    };

    // if user provided taken info, push into taken array and reduce qty accordingly
    if (newItem.takenBy && Number(newItem.takenQty) > 0) {
      itemObj.taken.push({
        user: newItem.takenBy,
        qty: Number(newItem.takenQty),
        date: newItem.takenDate || new Date().toISOString().slice(0, 10),
        returnDate: newItem.returnDate || null,
      });
    }

    // call parent to update data
    if (onAddItem) {
      onAddItem(container.id, selectedSlot, itemObj);
    } else {
      // fallback alert if parent didn't pass handler
      alert("onAddItem handler not provided in parent. Provide it to persist data.");
    }

    // reset and close
    setNewItem({
      id: "",
      name: "",
      quantity: 1,
      isReturnable: true,
      takenBy: "",
      takenQty: 0,
      takenDate: "",
      returnDate: "",
    });
    setIsAddModalOpen(false);
    setIsDetailModalOpen(false);
  };

  return (
    <>
      <div
        className="card shadow-sm my-3"
        style={{
          background:
            "linear-gradient(90deg, hsl(215, 25%, 12%) 0%, hsl(215, 25%, 10%) 100%)",
          color: "hsl(210, 40%, 98%)",
          border: "1px solid hsl(215, 20%, 25%)",
          borderRadius: "10px",
        }}
      >
        <div className="card-header d-flex flex-wrap justify-content-between align-items-center gap-2">
          <div className="d-flex align-items-center gap-3">
            <h5 className="mb-0 text-light">{container.name}</h5>
            <span className="text-muted small">
              {occupiedSlots}/{totalSlots} occupied
            </span>
          </div>

          <div className="d-flex align-items-center gap-2">
            <button
              className="btn btn-sm btn-outline-light"
              onClick={(e) => {
                e.stopPropagation();
                if (onAddSlot) onAddSlot(container.id);
              }}
              title="Add Slot"
            >
              +Slot
            </button>

            {onDeleteRack && (
              <button
                className="btn btn-sm btn-secondary border border-secondary"
                onClick={(e) => {
                  e.stopPropagation();
                  onDeleteRack(container.id);
                }}
                title="Delete Rack"
              >
                <Trash2 size={14} />
              </button>
            )}
          </div>
        </div>

        <div className="card-body">
          <div className="row g-3">
            {container.slots.map((slot) => {
              const slotId = `rack-${container.id}-slot-${slot.slotNumber}`;
              const visible = matchesFilter(slot);

              return (
                <div
                  key={`${container.id}-slot-${slot.slotNumber}`}
                  id={slotId}
                  className="col-12 col-sm-6 col-md-4 col-lg-3"
                  onClick={() => handleSlotClick(slot.slotNumber)}
                  style={{
                    cursor: "pointer",
                    opacity: visible ? 1 : 0.35,
                    transition: "opacity 0.15s ease, transform 0.12s ease",
                    position: "relative",
                  }}
                >
                  <SlotCard
                    slot={slot}
                    containerName={container.name}
                    containerId={container.id}
                    onDeleteSlot={(cId, sNum) => {
                      // If parent passed onDeleteSlot, call it; otherwise no-op
                      if (onDeleteSlot) onDeleteSlot(container.id, sNum);
                    }}
                    onAddItemButton={handleAddButtonFromCard}
                  />
                </div>
              );
            })}
          </div>
        </div>
      </div>

      {/* Details Modal (shows items list + remaining qty + taken info) */}
      <Modal
        show={isDetailModalOpen}
        onHide={() => setIsDetailModalOpen(false)}
        centered
        size="md"
      >
        <Modal.Header closeButton style={{ backgroundColor: "hsl(215,25%,12%)", color: "hsl(210,40%,98%)" }}>
          <Modal.Title>Slot {selectedSlot} - Details</Modal.Title>
        </Modal.Header>

        <Modal.Body style={{ backgroundColor: "hsl(215,25%,14%)", color: "hsl(210,40%,98%)" }}>
          {selectedSlotData && selectedSlotData.items && selectedSlotData.items.length > 0 ? (
            <>
              {selectedSlotData.items.map((it) => (
                <div key={it.id} className="mb-3 p-2 rounded" style={{ background: "rgba(255,255,255,0.02)" }}>
                  <div className="d-flex justify-content-between align-items-center">
                    <div>
                      <div className="fw-semibold">{it.name}</div>
                      <div className="small text-muted">Total: {it.quantity}</div>
                    </div>
                    <div className="text-end">
                      <div className="small">{it.isReturnable ? "Returnable" : "Non-returnable"}</div>
                      <div className="small text-info">Remaining: {remainingFor(it)}</div>
                    </div>
                  </div>

                  {/* taken entries */}
                  {it.taken && it.taken.length > 0 && (
                    <div className="mt-2 small text-muted">
                      <div className="fw-semibold">Taken history:</div>
                      {it.taken.map((t, i) => (
                        <div key={i} className="d-flex justify-content-between">
                          <div>
                            {t.user} — Qty: {t.qty} — {t.date}
                          </div>
                          <div>{t.returnDate ? `Return: ${t.returnDate}` : "-"}</div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              ))}

              <div className="d-flex justify-content-end">
                <Button
                  variant="outline-light"
                  onClick={() => {
                    // open add modal for adding new item to same slot
                    setIsDetailModalOpen(false);
                    setIsAddModalOpen(true);
                  }}
                >
                  + Add item
                </Button>
              </div>
            </>
          ) : (
            <p className="text-muted">No items in this slot.</p>
          )}
        </Modal.Body>
      </Modal>

      {/* Add Item Modal (Add + Take info in same modal) */}
      <Modal show={isAddModalOpen} onHide={() => setIsAddModalOpen(false)} centered size="md">
        <Modal.Header closeButton style={{ backgroundColor: "hsl(215,25%,12%)", color: "hsl(210,40%,98%)" }}>
          <Modal.Title>Add Item to Slot {selectedSlot}</Modal.Title>
        </Modal.Header>

        <Modal.Body style={{ backgroundColor: "hsl(215,25%,14%)", color: "hsl(210,40%,98%)" }}>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Item Name</Form.Label>
              <Form.Control type="text" value={newItem.name} onChange={(e) => setNewItem({ ...newItem, name: e.target.value })} placeholder="Item name" />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Quantity</Form.Label>
              <Form.Control type="number" min="1" value={newItem.quantity} onChange={(e) => setNewItem({ ...newItem, quantity: Number(e.target.value) })} />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Returnable?</Form.Label>
              <Form.Check
                type="switch"
                id="isReturnableSwitch"
                label={newItem.isReturnable ? "Yes" : "No"}
                checked={!!newItem.isReturnable}
                onChange={(e) => setNewItem({ ...newItem, isReturnable: e.target.checked })}
              />
            </Form.Group>

            <hr style={{ borderColor: "rgba(255,255,255,0.04)" }} />

            <div className="small text-muted mb-2">Optional — if someone already took some:</div>

            <Form.Group className="mb-3">
              <Form.Label>Taken by (optional)</Form.Label>
              <Form.Control type="text" value={newItem.takenBy} onChange={(e) => setNewItem({ ...newItem, takenBy: e.target.value })} placeholder="User name" />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Taken qty (optional)</Form.Label>
              <Form.Control type="number" min="0" value={newItem.takenQty} onChange={(e) => setNewItem({ ...newItem, takenQty: Number(e.target.value) })} />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Taken Date</Form.Label>
              <Form.Control type="date" value={newItem.takenDate} onChange={(e) => setNewItem({ ...newItem, takenDate: e.target.value })} />
            </Form.Group>

            {newItem.isReturnable && (
              <Form.Group className="mb-3">
                <Form.Label>Return Date (optional)</Form.Label>
                <Form.Control type="date" value={newItem.returnDate} onChange={(e) => setNewItem({ ...newItem, returnDate: e.target.value })} />
              </Form.Group>
            )}
          </Form>
        </Modal.Body>

        <Modal.Footer style={{ backgroundColor: "hsl(215,25%,12%)" }}>
          <Button variant="secondary" onClick={() => setIsAddModalOpen(false)}>
            Cancel
          </Button>

          <Button variant="primary" onClick={submitAddItem}>
            Add Item
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default ContainerGrid;
