// components/admin/dashboard/ItemDueCard.jsx
import React, { useEffect, useState } from "react";
import axios from "axios";

const ItemDueCard = ({ containers }) => {
  const [rows, setRows] = useState([]);

  const fetchDueDates = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("http://localhost:8000/api/manager/dueDates", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setRows(res.data.data || []);
    } catch (err) {
      console.error("Error fetching due items:", err);
    }
  };

  useEffect(() => {
    fetchDueDates();
  }, []);

  const formatDate = (date) =>
    date ? new Date(date).toLocaleDateString("en-IN") : "-";

  return (
    <div
      className="card shadow-sm"
      style={{
        background: "hsl(215,25%,12%)",
        border: "1px solid hsl(215,20%,25%)",
        color: "white",
        height: "100%",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <div
        className="card-header fw-bold"
        style={{
          background: "hsl(215,25%,18%)",
        }}
      >
        Item Due Dates
      </div>

      {/* Scrollable Area */}
      <div
        className="card-body p-0"
        style={{
          overflowY: "auto",
          flex: 1,
        }}
      >
        <table className="table table-dark mb-0" style={{ minWidth: "100%" }}>
          <thead
            style={{
              position: "sticky",
              top: 0,
              background: "hsl(215,25%,18%)",
              zIndex: 10,
            }}
          >
            <tr>
              <th>Item</th>
              <th>User</th>
              <th>Taken</th>
              <th>Return</th>
              <th>Days Left</th>
              <th>Overdue</th>
              <th></th>
            </tr>
          </thead>

          <tbody>
            {rows.length === 0 ? (
              <tr>
                <td colSpan="7" className="text-center text-muted py-3">
                  No due items
                </td>
              </tr>
            ) : (
              rows.map((r, i) => (
                <tr
                  key={r.borrowId}
                  style={{
                    background:
                      i % 2 === 0
                        ? "hsl(215,25%,14%)"
                        : "hsl(215,25%,16%)",
                  }}
                >
                  <td>{r.itemName || "Unknown"}</td>
                  <td>{r.userName}</td>
                  <td>{formatDate(r.takenDate)}</td>
                  <td>{formatDate(r.returnDate)}</td>
                  <td>{r.daysLeft}</td>
                  <td style={{ color: r.overdueDays > 0 ? "#ff4d4d" : "#fff" }}>
                    {r.overdueDays} days
                  </td>
                  <td>
                    <button className="btn btn-primary btn-sm">Reminder</button>
                  </td>
                </tr>
              ))
            )}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default ItemDueCard;
