import React, { useState, useEffect } from "react";
import { Outlet } from "react-router-dom";

import Navbar from "../../components/common/UniversalNavbar";
import Sidebar from "../../components/common/UniversalSidebar";

import { mockAdmin, mockManager } from "../../data/Mockdata";

const UniversalLayout = ({ role }) => {
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [isMobile, setIsMobile] = useState(window.innerWidth < 992);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  // Auto detect screen size
  useEffect(() => {
    const handleResize = () => setIsMobile(window.innerWidth < 992);
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Attach correct data based on role
  const getUserData = () => {
    if (role === "admin") return mockAdmin;
    if (role === "manager") return mockManager;
    return null; // user role has no name/email
  };

  return (
    <div
      className="d-flex position-relative"
      style={{
        minHeight: "100vh",
        backgroundColor: "hsl(215, 30%, 10%)",
        color: "hsl(210, 40%, 98%)",
        overflow: "hidden",
      }}
    >
      {/* SIDEBAR */}
      <UniversalSidebar
        role={role}
        isOpen={sidebarOpen}
        onClose={toggleSidebar}
      />

      {/* MAIN AREA */}
      <div
        className="d-flex flex-column flex-grow-1"
        style={{
          transition: "margin-left 0.3s ease",
          marginLeft: isMobile ? 0 : sidebarOpen ? "240px" : "70px",
          width: "100%",
        }}
      >
        {/* NAVBAR */}
        <UniversalNavbar
          role={role}
          data={getUserData()}
          onMenuClick={toggleSidebar}
          isMobile={isMobile}
        />

        {/* PAGE CONTENT */}
        <main
          className="flex-grow-1 p-4"
          style={{
            backgroundColor: "hsl(215, 25%, 14%)",
            overflowY: "auto",
          }}
        >
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default UniversalLayout;
