// components/admin/dashboard/LowStockCard.jsx
import React from "react";

const LowStockCard = ({ containers = [], onJumpToSlot }) => {
  const lowItems = [];

  // Extract low stock items from racks
  containers.forEach((rack) => {
    (rack.slots || []).forEach((slot) => {
      (slot.items || []).forEach((item) => {
        const totalTaken = (item.taken || []).reduce(
          (sum, t) => sum + Number(t.qty || 0),
          0
        );

        const remaining = Number(item.total || 0) - totalTaken;

        if (remaining <= 6) {
          lowItems.push({
            itemName: item.itemName,
            remaining,
            rackName: rack.rackName,
            slotNumber: slot.slotNumber,
            rackId: rack._id,
          });
        }
      });
    });
  });

  return (
    <div
      className="card shadow-sm"
      style={{
        background: "hsl(215, 25%, 12%)",
        border: "1px solid hsl(215, 20%, 25%)",
        color: "white",
        height: "100%",
        display: "flex",
        flexDirection: "column",
      }}
    >
      <div
        className="card-header fw-bold"
        style={{ background: "hsl(215, 25%, 18%)" }}
      >
        Limited Stock Items
      </div>

      {/* Scrollable Items */}
      <div
        className="card-body p-2"
        style={{
          flex: 1,
          overflowY: "auto",
        }}
      >
        {lowItems.length === 0 ? (
          <div className="text-center text-muted mt-3">No low-stock items</div>
        ) : (
          lowItems.map((it, idx) => (
            <div
              key={idx}
              className="p-3 mb-3 rounded d-flex justify-content-between align-items-center"
              style={{
                background: "hsl(215, 25%, 16%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              <div>
                <div className="fw-semibold">{it.itemName}</div>

                <div className="small text-muted">
                  Remaining:{" "}
                  <span
                    style={{
                      color: it.remaining <= 2 ? "#f44336" : "#ffd166", // red or yellow
                      fontWeight: "bold",
                    }}
                  >
                    {it.remaining}
                  </span>
                </div>

                <div className="text-muted small">
                  {it.rackName} â€¢ Slot {it.slotNumber}
                </div>
              </div>

              {/* Move Button */}
              <button
                className="btn btn-outline-primary btn-sm"
                style={{ whiteSpace: "nowrap" }}
                onClick={() => onJumpToSlot(it.rackId, it.slotNumber)}
              >
                Move >>>
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LowStockCard;
