    const items =
      Array.isArray(slot.items) && slot.items.length > 0
        ? slot.items
        : slot.item
        ? [slot.item]
        : [];
    const first = items[0];

    switch (activeFilter) {
      case "Returnable":
        return first && first.isReturnable;
      case "Non-returnable":
        return first && !first.isReturnable;
      case "Occupied":
        return items.length > 0;
      case "Empty":
        return items.length === 0;
      default:
        return true;
    }
  };

