// components/admin/ContainerSummaryCard.jsx
//------------------------------------------------------
// FIXED for new items[] structure
// Shows:
//   - Total slots
//   - Occupied slots (slots with at least 1 item)
//   - Available slots
//   - Occupancy percent
//   - Click → navigate to /admin/container/:id
//------------------------------------------------------

import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Package } from "lucide-react";
import { useNavigate } from "react-router-dom";

const ContainerSummaryCard = ({ container }) => {
  const navigate = useNavigate();

  // FIX: Slot is occupied if items array has length > 0
  const occupiedSlots = container.slots.filter(
    (s) => s.items && s.items.length > 0
  ).length;

  const totalSlots = container.slots.length;
  const availableSlots = totalSlots - occupiedSlots;

  const occupancyRate =
    totalSlots > 0 ? Math.round((occupiedSlots / totalSlots) * 100) : 0;

  return (
    <div
      className="card p-3 shadow-sm"
      style={{
        background:
          "linear-gradient(90deg, hsl(215, 25%, 12%) 0%, hsl(215, 25%, 10%) 100%)",
        color: "hsl(210, 40%, 98%)",
        border: "1px solid hsl(215, 20%, 25%)",
        cursor: "pointer",
      }}
      onClick={() => navigate(`/admin/container/${container.id}`)}
    >
      <div className="d-flex align-items-center gap-3 mb-3">
        <div
          className="rounded bg-light d-flex justify-content-center align-items-center"
          style={{ width: "45px", height: "45px" }}
        >
          <Package size={22} className="text-primary" />
        </div>

        <div>
          <h5 className="mb-0">{container.name}</h5>
          <small className="text-muted">{container.id}</small>
        </div>
      </div>

      <div className="d-flex justify-content-between mt-3">
        <div>
          <p className="m-0">
            <strong>{totalSlots}</strong> Slots
          </p>
          <p className="m-0 text-success">
            <strong>{occupiedSlots}</strong> Occupied
          </p>
          <p className="m-0 text-warning">
            <strong>{availableSlots}</strong> Available
          </p>
        </div>

        <div className="text-end">
          <h4 className="mb-0">{occupancyRate}%</h4>
          <small className="text-muted">Occupancy</small>
        </div>
      </div>
    </div>
  );
};

export default ContainerSummaryCard;
