// components/admin/ContainerGrid.jsx
import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import SlotCard from "./SlotCard";
import { Modal, Button, Form } from "react-bootstrap";
import { Trash2,  Plus } from "lucide-react"; // using lucide icon for small trash

const ContainerGrid = ({ container, activeFilter = "All", onAddSlot, onDeleteRack, onDeleteSlot, onExternalSlotClick }) => {
  const occupiedSlots = container.slots.filter((s) => s.item !== null).length;
  const totalSlots = container.slots.length;

  const [selectedSlot, setSelectedSlot] = useState(null);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [isDetailModalOpen, setIsDetailModalOpen] = useState(false);
  const [newItem, setNewItem] = useState({
    name: "",
    takenBy: "",
    status: "occupied",
    returnDate: "",
  });

  const handleSlotClick = (slotNumber) => {
    const slot = container.slots.find((s) => s.slotNumber === slotNumber);
    setSelectedSlot(slotNumber);

    // keep existing modal behavior
    if (slot?.item) setIsDetailModalOpen(true);
    else setIsAddModalOpen(true);

    // inform parent if needed
    if (onExternalSlotClick) onExternalSlotClick(container.id, slotNumber);
  };

  const handleAddItem = () => {
    alert(`${newItem.name} added to Slot ${selectedSlot}`);
    setIsAddModalOpen(false);
    setNewItem({ name: "", takenBy: "", status: "occupied", returnDate: "" });
  };

  const selectedSlotData = container.slots.find((s) => s.slotNumber === selectedSlot);

  // filter helper - used to dim non-matching slots
  const matchesFilter = (slot) => {
    const item = slot.item;
    switch (activeFilter) {
      case "All":
        return true;
      case "Returnable":
        return item && item.isReturnable === true;
      case "Non-returnable":
        return item && item.isReturnable === false;
      case "Occupied":
        return !!item;
      case "Empty":
        return !item;
      default:
        return true;
    }
  };

  return (
    <>
      {/* Main Container Card */}
      <div
        className="card shadow-sm my-3"
        style={{
          background:
            "linear-gradient(90deg, hsl(215, 25%, 12%) 0%, hsl(215, 25%, 10%) 100%)",
          color: "hsl(210, 40%, 98%)",
          border: "1px solid hsl(215, 20%, 25%)",
          borderRadius: "10px",
        }}
      >
        {/* Header */}
        <div className="card-header d-flex flex-wrap justify-content-between align-items-center gap-2">
          <div className="d-flex align-items-center gap-3">
            <h5 className="mb-0 text-light">{container.name}</h5>
            <span className="text-muted small">
              {occupiedSlots}/{totalSlots} occupied
            </span>
          </div>

          <div className="d-flex align-items-center gap-2">
            {/* Add Slot button (calls parent handler) */}
            <button
              className="btn btn-sm btn-outline-light"
              onClick={(e) => {
                e.stopPropagation();
                if (onAddSlot) onAddSlot(container.id);
              }}
              title="Add Slot"
            >
              
              +Slot
            </button>

            {/* Delete Rack button (small trash) */}
            {onDeleteRack && (
              <button
                className="btn btn-sm btn-secondary border border-secondary"
                onClick={(e) => {
                  e.stopPropagation();
                  onDeleteRack(container.id);
                }}
                title="Delete Rack"
              >
                <Trash2 size={14} />
              </button>
            )}
          </div>
        </div>

        {/* Body */}
        <div className="card-body">
          <div className="row g-3">
            {container.slots.map((slot) => {
              const slotId = `rack-${container.id}-slot-${slot.slotNumber}`;
              const visible = matchesFilter(slot);

              return (
                <div
                  key={`${container.id}-slot-${slot.slotNumber}`}
                  id={slotId} // <-- ID for scrolling/jump
                  className="col-12 col-sm-6 col-md-4 col-lg-3"
                  onClick={() => handleSlotClick(slot.slotNumber)}
                  style={{
                    cursor: "pointer",
                    opacity: visible ? 1 : 0.35,
                    transition: "opacity 0.15s ease, transform 0.12s ease",
                    position: "relative",
                  }}
                >
                  {/* slot card content */}
                  <div style={{ position: "relative" }}>
                    {/* Small delete slot button (Option 1)
                    {onDeleteSlot && (
                      <button
                        onClick={(e) => {
                          e.stopPropagation(); // prevent opening modal
                          onDeleteSlot(container.id, slot.slotNumber);
                        }}
                        title="Delete Slot"
                        className="btn btn-sm"
                        style={{
                          position: "absolute",
                          bottom: 8,
                          right: 12,
                          zIndex: 5,
                          padding: "4px 6px",
                          lineHeight: 1,
                          backgroundColor: "transparent",
                          border: "none",
                          color: "hsla(200, 11%, 32%, 1.00)",
                        }}
                      >
                        <Trash2 size={14} />
                      </button>
                    )} */}

                    <SlotCard slot={slot} containerName={container.name} />
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    /* End Main Container Card */
      {/* Item Details Modal */}
      <Modal
        show={isDetailModalOpen}
        onHide={() => setIsDetailModalOpen(false)}
        centered
        size="md"
      >
        <Modal.Header
          closeButton
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "hsl(210, 40%, 98%)",
          }}
        >
          <Modal.Title>Slot {selectedSlot} - Item Details</Modal.Title>
        </Modal.Header>
        <Modal.Body
          style={{
            backgroundColor: "hsl(215, 25%, 14%)",
            color: "hsl(210, 40%, 98%)",
          }}
        >
          {selectedSlotData?.item ? (
            <>
              <div className="mb-3">
                <Form.Label>Item Name</Form.Label>
                <p className="fw-semibold mb-0">{selectedSlotData.item.name}</p>
              </div>
              <div className="mb-3">
                <Form.Label>Status</Form.Label>
                <p className="text-capitalize mb-0">
                  {selectedSlotData.item.status.replace("-", " ")}
                </p>
              </div>
              <div className="mb-3">
                <Form.Label>Taken By</Form.Label>
                <p className="mb-0">{selectedSlotData.item.takenBy}</p>
              </div>
              {selectedSlotData.item.takenDate && (
                <div className="mb-3">
                  <Form.Label>Taken Date</Form.Label>
                  <p className="mb-0">
                    {new Date(selectedSlotData.item.takenDate).toLocaleDateString()}
                  </p>
                </div>
              )}
              {selectedSlotData.item.returnDate && (
                <div className="mb-3">
                  <Form.Label>Expected Return</Form.Label>
                  <p className="mb-0">
                    {new Date(selectedSlotData.item.returnDate).toLocaleDateString()}
                  </p>
                </div>
              )}
            </>
          ) : (
            <p className="text-muted">No item found in this slot.</p>
          )}
        </Modal.Body>
      </Modal>

      {/* Add Item Modal */}
      <Modal
        show={isAddModalOpen}
        onHide={() => setIsAddModalOpen(false)}
        centered
        size="md"
      >
        <Modal.Header
          closeButton
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "hsl(210, 40%, 98%)",
          }}
        >
          <Modal.Title>Add Item to Slot {selectedSlot}</Modal.Title>
        </Modal.Header>
        <Modal.Body
          style={{
            backgroundColor: "hsl(215, 25%, 14%)",
            color: "hsl(210, 40%, 98%)",
          }}
        >
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Item Name</Form.Label>
              <Form.Control
                type="text"
                value={newItem.name}
                onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
                placeholder="Enter item name"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Taken By</Form.Label>
              <Form.Control
                type="text"
                value={newItem.takenBy}
                onChange={(e) => setNewItem({ ...newItem, takenBy: e.target.value })}
                placeholder="Enter user name"
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Status</Form.Label>
              <Form.Select
                value={newItem.status}
                onChange={(e) => setNewItem({ ...newItem, status: e.target.value })}
              >
                <option value="occupied">Returnable</option>
                <option value="non-returnable">Non-Returnable</option>
              </Form.Select>
            </Form.Group>

            {newItem.status === "occupied" && (
              <Form.Group className="mb-3">
                <Form.Label>Return Date</Form.Label>
                <Form.Control
                  type="date"
                  value={newItem.returnDate}
                  onChange={(e) => setNewItem({ ...newItem, returnDate: e.target.value })}
                />
              </Form.Group>
            )}
          </Form>
        </Modal.Body>
        <Modal.Footer
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            borderTop: "1px solid hsl(215, 20%, 25%)",
          }}
        >
          <Button
            variant="secondary"
            onClick={() => setIsAddModalOpen(false)}
            style={{
              backgroundColor: "hsl(215, 25%, 18%)",
              border: "none",
            }}
          >
            Cancel
          </Button>
          <Button variant="primary" onClick={handleAddItem}>
            Add Item
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default ContainerGrid;
