import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Package, Calendar, User, Trash2 } from "lucide-react";

const SlotCard = ({ slot, containerName, onDeleteSlot }) => {

  const { slotNumber } = slot;

  // NEW: extract first item from slot.items
  const item = slot.items && slot.items.length > 0 ? slot.items[0] : null;

  // Badge rendering logic
  const getStatusBadge = () => {
    if (!item) {
      return <span className="badge bg-secondary">Empty</span>;
    }
    if (!item.isReturnable) {
      return <span className="badge bg-warning text-dark">Non-Returnable</span>;
    }
    return <span className="badge bg-success">Occupied</span>;
  };

  return (
    <div
      className={`card p-3 mb-3 border-1 shadow-sm position-relative ${
        !item ? "opacity-75" : ""
      }`}
      style={{
        cursor: "pointer",
        transition: "0.3s",
        borderColor: !item ? "#ccc" : "#0d6efd50",
      }}
    >
      {/* Header */}
      <div className="d-flex justify-content-between align-items-start mb-3">
        <div className="d-flex align-items-center gap-2">
          <div
            className="rounded bg-light d-flex justify-content-center align-items-center"
            style={{ width: "40px", height: "40px" }}
          >
            <Package size={18} className="text-primary" />
          </div>
          <div>
            <p className="text-muted small mb-0">Slot {slotNumber}</p>
            {containerName && (
              <p className="fw-semibold mb-0">{containerName}</p>
            )}
          </div>
        </div>
        {getStatusBadge()}
      </div>

      {/* Body */}
      {item ? (
        <div>
          <p className="fw-semibold mb-1">{item.name}</p>

          {/* Quantity */}
          <p className="small text-info mb-1">Qty: {item.quantity}</p>

          {/* Taken details */}
          {item.taken && item.taken.length > 0 && (
            <>
              <div className="d-flex align-items-center gap-2 text-muted small">
                <User size={14} />
                <span>{item.taken[0].user} (Qty: {item.taken[0].qty})</span>
              </div>

              {item.taken[0].returnDate && (
                <div className="d-flex align-items-center gap-2 text-muted small mt-1">
                  <Calendar size={14} />
                  <span>Return: {item.taken[0].returnDate}</span>
                </div>
              )}
            </>
          )}

          {!item.isReturnable && (
            <p className="text-warning small mt-2 mb-0">
              This item is non-returnable
            </p>
          )}
        </div>
      ) : (
        <p className="text-muted small mb-0">Available slot</p>
      )}

      {/* DELETE SLOT BUTTON */}
      <button
        onClick={(e) => {
          e.stopPropagation();
          if (onDeleteSlot) onDeleteSlot(slotNumber);
        }}
        className="btn p-1 position-absolute"
        title="Delete Slot"
        style={{
          bottom: "8px",
          right: "10px",
          background: "transparent",
          border: "none",
          color: "hsla(0, 8%, 37%, 1.00)",
        }}
      >
        <Trash2 size={18} />
      </button>
    </div>
  );
};

export default SlotCard;
