import React from "react";
import { Card, Button, Row, Col, Badge } from "react-bootstrap";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEnvelope, faCalendar, faCheck, faTimes } from "@fortawesome/free-solid-svg-icons";

const users = [
  { name: "Alice Cooper", email: "alice.cooper@example.com", date: "2025-01-14" },
  { name: "Bob Wilson", email: "bob.wilson@example.com", date: "2025-01-15" },
  { name: "Carol Martinez", email: "carol.martinez@example.com", date: "2025-01-15" },
];

const PendingApprovals = () => {
  return (
    <Card className="bg-dark text-white border-0 shadow-sm p-4 mt-4">
      <div className="d-flex justify-content-between align-items-center mb-3">
        <h4 className="fw-bold">Pending User Approvals</h4>
        <Badge bg="secondary" pill>
          {users.length} pending
        </Badge>
      </div>

      {users.map((user, index) => (
        <Card key={index} className="bg-secondary border-0 mb-3 text-white p-3 rounded-4">
          <Row className="align-items-center">
            <Col md={6}>
              <h5 className="fw-semibold">{user.name}</h5>
              <div className="text-light small">
                <FontAwesomeIcon icon={faEnvelope} className="me-2" />
                {user.email}
                <span className="ms-3">
                  <FontAwesomeIcon icon={faCalendar} className="me-2" />
                  {user.date}
                </span>
              </div>
            </Col>
            <Col md={6} className="text-md-end mt-3 mt-md-0">
              <Button variant="success" className="me-2 px-4">
                <FontAwesomeIcon icon={faCheck} className="me-2" />
                Approve
              </Button>
              <Button variant="danger" className="px-4">
                <FontAwesomeIcon icon={faTimes} className="me-2" />
                Reject
              </Button>
            </Col>
          </Row>
        </Card>
      ))}
    </Card>
  );
};

export default PendingApprovals;
