import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import api from "../../api/axios";
import NotificationToast from "../common/NotificationToast";

const ManagerPermissionModal = ({ show, onClose, request, onGiven }) => {
  const [rackId, setRackId] = useState("");
  const [slotId, setSlotId] = useState("");
  const [racks, setRacks] = useState([]);
  const [filteredRacks, setFilteredRacks] = useState([]);
  const [filteredSlots, setFilteredSlots] = useState([]);

  const [toast, setToast] = useState({
    show: false,
    message: "",
    bg: "success",
  });

  useEffect(() => {
    if (show) {
      fetchRacks();
      setRackId("");
      setSlotId("");
      setFilteredRacks([]);
      setFilteredSlots([]);
    }
  }, [show]);

  const fetchRacks = async () => {
    try {
      const res = await api.get("/racks/all");
      setRacks(res.data.data || []);
    } catch (err) {
      console.error(err);
    }
  };

  const filterRackSuggestions = (value) => {
    setRackId(value);
    if (!value.trim()) return setFilteredRacks([]);

    const result = racks.filter(r =>
      r.name.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredRacks(result);
  };

  const selectRack = (rack) => {
    setRackId(rack.name);
    setFilteredRacks([]);

    const availableSlots = rack.slots || [];
    setFilteredSlots(availableSlots);
  };

  const selectSlot = (slot) => {
    setSlotId(slot.slotId);
    setFilteredSlots([]);
  };

  const handleGiveItem = async () => {
    const selectedRack = racks.find(r => r.name === rackId);
    if (!selectedRack) {
      return setToast({ show: true, message: "Rack not found!", bg: "danger" });
    }

    if (!slotId) {
      return setToast({ show: true, message: "Select a slot!", bg: "danger" });
    }

    try {
      await api.post(`/manager/giveItem/${request._id}`, {
        rackId: selectedRack.id,
        slotId,
      });

      setToast({ show: true, message: "Item successfully given!", bg: "success" });

      setTimeout(() => {
        onGiven();
        onClose();
      }, 1000);
    } catch (err) {
      console.error(err);
      setToast({
        show: true,
        message: "Failed to issue item!",
        bg: "danger",
      });
    }
  };

  if (!request) return null;

  return (
    <Modal show={show} onHide={onClose} centered>
      <Modal.Header
        closeButton
        className="text-white"
        style={{ background: "hsl(215,25%,10%)" }}
      >
        <Modal.Title>Give Item To User</Modal.Title>
      </Modal.Header>

      <Modal.Body
        className="text-white"
        style={{ background: "hsl(215,25%,20%)" }}
      >
        <p><strong>User:</strong> {request.user.name}</p>
        <p><strong>Email:</strong> {request.user.email}</p>
        <hr />
        <p><strong>Item:</strong> {request.itemName}</p>
        <p><strong>Qty:</strong> {request.quantity}</p>
        <p><strong>Project:</strong> {request.project}</p>

        <hr />

        {/* Rack Input + Suggestions */}
        <Form.Group className="mt-2">
          <Form.Label>Rack name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Rack Name"
            value={rackId}
            onChange={(e) => filterRackSuggestions(e.target.value)}
            style={{
              background: "hsl(215,25%,12%)",
              borderColor: "hsl(215,20%,35%)",
              color: "white",
            }}
          />
        </Form.Group>

        {filteredRacks.length > 0 && (
          <ul className="list-group mt-1">
            {filteredRacks.map((rack) => (
              <li
                key={rack.id}
                className="list-group-item list-group-item-dark"
                style={{ cursor: "pointer" }}
                onClick={() => selectRack(rack)}
              >
                {rack.name}
              </li>
            ))}
          </ul>
        )}

        {/* Slot Input + Suggestions */}
        <Form.Group className="mt-3">
          <Form.Label>Slot number</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Slot number"
            value={slotId}
            onChange={(e) => setSlotId(e.target.value)}
            style={{
              background: "hsl(215,25%,12%)",
              borderColor: "hsl(215,20%,35%)",
              color: "white",
            }}
          />
        </Form.Group>

        {filteredSlots.length > 0 && (
          <ul className="list-group mt-1">
            {filteredSlots.map((slot) => (
              <li
                key={slot.slotId}
                className="list-group-item list-group-item-secondary"
                style={{ cursor: "pointer" }}
                onClick={() => selectSlot(slot)}
              >
                Slot {slot.slotNumber} — {slot.items?.length} items
              </li>
            ))}
          </ul>
        )}
      </Modal.Body>

      <Modal.Footer
        className="bg-dark"
        style={{ borderTop: "1px solid hsl(215,20%,25%)" }}
      >
        <Button variant="success" onClick={handleGiveItem}>
          Issue Item ✓
        </Button>
      </Modal.Footer>

      <NotificationToast
        show={toast.show}
        onClose={() => setToast({ ...toast, show: false })}
        message={toast.message}
        bg={toast.bg}
      />
    </Modal>
  );
};

export default ManagerPermissionModal;
