import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import api from "../../api/axios";
import NotificationToast from "../common/NotificationToast";

const ManagerPermissionModal = ({ show, onClose, request, onGiven }) => {
  const [availability, setAvailability] = useState([]);
  const [selectedSlot, setSelectedSlot] = useState(null);
  const [issueQty, setIssueQty] = useState(0);

  const [toast, setToast] = useState({
    show: false,
    message: "",
    bg: "success",
  });

  /* =========================
     FETCH ITEM AVAILABILITY
     ========================= */
  useEffect(() => {
    if (!request || !show) return;

    setAvailability([]);
    setSelectedSlot(null); // reset on open

    api
      .get(`/manager/itemAvailability/${request.itemName}`)
      .then((res) => setAvailability(res.data.data || []))
      .catch(() => setAvailability([]));
  }, [request, show]);

    /* =========================
     AUTO SELECT BEST SLOT
     ========================= */
  useEffect(() => {
    if (availability.length > 0) {
      const bestSlot = availability.reduce((max, curr) =>
        curr.remaining > max.remaining ? curr : max
      );
      setSelectedSlot(bestSlot);
      
    } else {
      setSelectedSlot(null);
      setIssueQty(0);
    }
  }, [availability]);

  if (!request) return null;


  /* =========================
     FORMAT RETURN DATE
     ========================= */
  const formattedReturnDate =
    request.isReturnable && request.returnDate
      ? new Date(request.returnDate).toLocaleDateString("en-GB", {
        day: "2-digit",
        month: "short",
        year: "numeric",
      })
      : null;

      

  /* =========================
     ISSUE ITEM HANDLER
     ========================= */
  const handleGiveItem = async () => {
    if (!selectedSlot) {
      setToast({
        show: true,
        message: "Please select a rack & slot",
        bg: "danger",
      });
      return;
    }

    if (selectedSlot.remaining < request.quantity) {
      setToast({
        show: true,
        message: `Only ${selectedSlot.remaining} available`,
        bg: "danger",
      });
      return;
    }

    try {
      await api.post(`/manager/giveItem/${request._id}`, {
        rackName: selectedSlot.rackName,
        slotNumber: selectedSlot.slotNumber,
      });

      setToast({
        show: true,
        message: "Item successfully issued!",
        bg: "success",
      });

      setTimeout(() => {
        onGiven();
        onClose();
      }, 800);
    } catch (err) {
      console.error(err);
      setToast({
        show: true,
        message: "Failed to issue item!",
        bg: "danger",
      });
    }
    

  };

  /* =========================
     UI
     ========================= */
  return (
    <Modal show={show} onHide={onClose} centered>
      <Modal.Header
        closeButton
        className="text-white"
        style={{ background: "hsl(215,25%,10%)" }}
      >
        <Modal.Title>Give Item To User</Modal.Title>
      </Modal.Header>

      <Modal.Body
        className="text-white"
        style={{ background: "hsl(215,25%,20%)" }}
      >
        {/* USER INFO */}
        <p><strong>User:</strong> {request.user.name}</p>
        <p><strong>Email:</strong> {request.user.email}</p>
        <hr />

        {/* ITEM INFO */}
        <p><strong>Item:</strong> {request.itemName}</p>
        <p><strong>Qty Requested:</strong> {request.quantity}</p>
        <p><strong>Project:</strong> {request.project}</p>

        <p>
          <strong>Returnable:</strong>{" "}
          {request.isReturnable ? "Yes" : "No"}
        </p>

        {formattedReturnDate && (
          <p><strong>Return Date:</strong> {formattedReturnDate}</p>
        )}

        <p>
          <strong>Message:</strong>{" "}
          {request.message?.trim() || "-"}
        </p>

        <hr />
        {/* SLOT SELECTION */}
        
        {/* <p className="text-secondary"><i >Item Present in Rack & Slot</i></p> */}
        
        <p>
          Rack Name: <strong>{selectedSlot?.rackName || "-"}</strong>
        </p>

        <p>
          Slot Name: <strong>{selectedSlot?.slotName || "-"}</strong>
        </p> 
        {selectedSlot && (
          <p>
            Remaining Quantity: <strong>{selectedSlot.remaining}</strong>
          </p>
        )}

        <hr/>
      {/* <p>OR</p>
        
        <Form.Group>
          
          <Form.Label>Select Rack & Slot</Form.Label>

          <Form.Select
            value={selectedSlot?.slotId || ""}
            onChange={(e) => {
              const slot = availability.find(
                (a) => a.slotId === e.target.value
              );
              setSelectedSlot(slot);
            }}
            style={{
              background: "hsl(215,25%,12%)",
              borderColor: "hsl(215,20%,35%)",
              color: "white",
            }}
          >
            <option value="">-- Select Rack / Slot --</option>

            {availability.map((a, idx) => (
              <option key={idx} value={a.slotId}>
                {a.rackName} → {a.slotName} (Remaining: {a.remaining})
              </option>
            ))}
          </Form.Select>
        </Form.Group> */}

        {/* WARNINGS */}
        {selectedSlot && selectedSlot.remaining < request.quantity && (
          <p className="text-danger mt-2">
            Only {selectedSlot.remaining} available
          </p>
        )}

        {availability.length === 0 && (
          <p className="text-warning mt-2">
            ⚠️ Item not available in any rack
          </p>
           
        )}
      </Modal.Body>

      <Modal.Footer
        className="bg-dark"
        style={{ borderTop: "1px solid hsl(215,20%,25%)" }}
      >
        <Button
          variant="success"
          onClick={handleGiveItem}
          disabled={
            !selectedSlot ||
            selectedSlot.remaining < request.quantity
          }
        >
          Issue Item ✓
        </Button>
      </Modal.Footer>

      {/* TOAST */}
      <NotificationToast
        show={toast.show}
        onClose={() => setToast({ ...toast, show: false })}
        message={toast.message}
        bg={toast.bg}
      />
    </Modal>
  );
};

export default ManagerPermissionModal;
