// components/admin/racks/SlotCard.jsx
// CLEAN + FIXED + FULLY COMPATIBLE WITH items[] ARRAY
//------------------------------------------------------

import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Package, Trash2, Plus } from "lucide-react";

/*
  Props:
    - slot: { slotNumber, items: [ { id, name, quantity, isReturnable, taken[] } ] }
    - containerName
    - containerId
    - onDeleteSlot(containerId, slotNumber)
    - onAddItemButton(containerId, slotNumber)
*/

const SlotCard = ({
  slot,
  containerName,
  containerId,
  onDeleteSlot,
  onAddItemButton,
}) => {
  const { slotNumber, items = [] } = slot;

  // If there is at least 1 item → first item is shown
  const firstItem = items.length > 0 ? items[0] : null;

  // BADGE LOGIC
  const getStatusBadge = () => {
    if (!firstItem) return <span className="badge bg-secondary">Empty</span>;
    if (!firstItem.isReturnable)
      return (
        <span className="badge bg-warning text-dark">Non-returnable</span>
      );
    return <span className="badge bg-success">Occupied</span>;
  };

  return (
    <div
      className={`card p-3 mb-3 border-1 shadow-sm position-relative ${
        !firstItem ? "opacity-75" : ""
      }`}
      style={{
        cursor: "pointer",
        transition: "0.3s",
        borderColor: !firstItem ? "#ccc" : "#0d6efd50",
        backgroundColor: "hsl(215, 25%, 14%)",
        color: "white",
      }}
    >
      {/* Header */}
      <div className="d-flex justify-content-between align-items-start mb-3">
        <div className="d-flex align-items-center gap-2">
          <div
            className="rounded bg-light d-flex justify-content-center align-items-center"
            style={{ width: "40px", height: "40px" }}
          >
            <Package size={18} className="text-primary" />
          </div>

          <div>
            <p className="text-muted small mb-0">Slot {slotNumber}</p>
            {containerName && <p className="fw-semibold mb-0">{containerName}</p>}
          </div>
        </div>

        {getStatusBadge()}
      </div>

      {/* =============== BODY =============== */}
      {firstItem ? (
        <div>
          <p className="fw-semibold mb-1">{firstItem.name}</p>
          <p className="small text-info mb-1">
            Total Qty: {firstItem.quantity}
          </p>

          {/* Multiple items notice */}
          {items.length > 1 && (
            <p className="text-muted small mt-2 mb-0">
              {items.length} item types in this slot
            </p>
          )}
        </div>
      ) : (
        <p className="text-muted small mb-0">Empty slot</p>
      )}

      {/* =============== CONTROLS (BOTTOM RIGHT) =============== */}
      <div
        style={{
          position: "absolute",
          bottom: 8,
          right: 8,
          display: "flex",
          gap: 8,
        }}
      >
        {/* Add Item */}
        <button
          onClick={(e) => {
            e.stopPropagation();
            if (onAddItemButton) onAddItemButton(containerId, slotNumber);
          }}
          title="Add item"
          className="btn p-1"
          style={{
            background: "transparent",
            border: "none",
            color: "hsla(210, 40%, 98%, 0.9)",
          }}
        >
          <Plus size={18} />
        </button>

        {/* Delete Slot */}
        <button
          onClick={(e) => {
            e.stopPropagation();
            if (onDeleteSlot) onDeleteSlot(containerId, slotNumber);
          }}
          title="Delete Slot"
          className="btn p-1"
          style={{
            background: "transparent",
            border: "none",
            color: "hsla(0, 8%, 37%, 1)",
          }}
        >
          <Trash2 size={18} />
        </button>
      </div>
    </div>
  );
};

export default SlotCard;
