import React, { useState, useContext } from 'react';
import { Link, Navigate } from 'react-router-dom';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { FaUser, FaLock } from 'react-icons/fa';
import { AuthContext } from '../context/AuthContext.jsx';

const Loginpage = () => {
  const { currentUser, login } = useContext(AuthContext);

  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });

  if (user) return <Navigate to="/AdminDashboard" />;

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await login(formData.email, formData.password);
  };

  const quickLogin = (role) => {
    const credentials = {
      admin: { email: 'admin@slot.com', password: 'password123' },
      manager: { email: 'manager@slot.com', password: 'password123' },
      user: { email: 'user@slot.com', password: 'password123' }
    };
    setFormData(credentials[role]);
  };

  return (
    <div className="bg-dark min-vh-100 d-flex align-items-center text-light">
      <Container>
        <Row className="justify-content-center">
          <Col xs={12} md={6} lg={4}>
            <div className="bg-secondary p-4 rounded shadow">
              <h2 className="text-center mb-4">Login</h2>
              <Form onSubmit={handleSubmit}>
                <Form.Group className="mb-3" controlId="formEmail">
                  <Form.Label>
                    <FaUser className="me-2" />
                    Email address
                  </Form.Label>
                  <Form.Control
                    type="email"
                    name="email"
                    placeholder="Enter email"
                    value={formData.email}
                    onChange={handleChange}
                    required
                    className="bg-dark text-light border-0"
                  />
                </Form.Group>

                <Form.Group className="mb-4" controlId="formPassword">
                  <Form.Label>
                    <FaLock className="me-2" />
                    Password
                  </Form.Label>
                  <Form.Control
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                    required
                    className="bg-dark text-light border-0"
                  />
                </Form.Group>

                <Button type="submit" variant="primary" className="w-100 fw-semibold">
                  Login
                </Button>

                <p className="mt-3 text-center">
                  Donâ€™t have an account?{' '}
                  <Link to="/register" className="text-light fw-bold">
                    Register here
                  </Link>
                </p>
              </Form>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default Loginpage;
