const SlotCard = ({ slot, containerName, onDeleteSlot }) => {
  const { slotNumber, items } = slot;
  const hasItems = items && items.length > 0;
  const item = hasItems ? items[0] : null; // show first item for now

  // Badge logic
  const getStatusBadge = () => {
    if (!hasItems) return <span className="badge bg-secondary">Empty</span>;
    if (!item.isReturnable) return <span className="badge bg-warning text-dark">Non-Returnable</span>;
    return <span className="badge bg-success">Occupied</span>;
  };

  return (
    <div className={`card p-3 mb-3 shadow-sm position-relative`}>

      {/* Header */}
      <div className="d-flex justify-content-between">
        <div>
          <p className="text-muted small mb-0">Slot {slotNumber}</p>
          <p className="fw-semibold mb-0">{containerName}</p>
        </div>
        {getStatusBadge()}
      </div>

      {/* If slot has items */}
      {hasItems ? (
        <>
          <p className="fw-bold mt-2">{item.name}</p>
          <p className="small text-muted">Total Qty: {item.quantity}</p>

          {item.taken?.length > 0 && (
            <>
              <p className="small text-muted mb-1">
                Taken by: {item.taken[0].user} ({item.taken[0].qty})
              </p>
              <p className="small text-muted">
                Date: {item.taken[0].date}
                {item.taken[0].returnDate && ` | Return: ${item.taken[0].returnDate}`}
              </p>
            </>
          )}
        </>
      ) : (
        <p className="text-muted small">Available slot</p>
      )}

      {/* DELETE SLOT */}
      {onDeleteSlot && (
        <button
          className="btn btn-sm position-absolute"
          style={{ bottom: 8, right: 8 }}
          onClick={(e) => {
            e.stopPropagation();
            onDeleteSlot(slotNumber);
          }}
        >
          <Trash2 size={16} />
        </button>
      )}
    </div>
  );
};
