// src/pages/users/UserHistory.jsx
import React from "react";

const UserHistory = ({ containers = [], currentUserEmail }) => {
  // Build list of taken records matching user
  const history = [];

  containers.forEach((c) => {
    c.slots.forEach((s) => {
      (s.items || []).forEach((it) => {
        (it.taken || []).forEach((t) => {
          // In your mock data taken.user may be a name — we'll include both:
          // match by email if available in taken object, else by name match with mockUsers could be improved.
          // For simplicity: include all records where taken.user === currentUserEmail OR contains currentUserEmail as substring OR taken.user matches common name 'User A'
          const taker = t.user || "";
          if (taker.toLowerCase().includes((currentUserEmail || "").split("@")[0].toLowerCase()) || taker.toLowerCase().includes("user") ) {
            history.push({
              itemName: it.name,
              qty: t.qty,
              project: t.project || "-",
              isReturnable: it.isReturnable,
              takenDate: t.date,
              returnDate: t.returnDate,
              status: t.returnDate ? (new Date(t.returnDate) < new Date() ? "Overdue" : "Not Returned") : "Returned",
            });
          }
        });
      });
    });
  });

  return (
    <div className="container my-4">
      <h2 className="fw-bold mb-2">My History</h2>
      <p className="text-muted">Items you have taken</p>

      <div className="card p-3" style={{ background: "hsl(215,25%,12%)", border: "1px solid hsl(215,20%,25%)", color: "white" }}>
        {history.length === 0 ? (
          <p className="text-muted">No history records found for you.</p>
        ) : (
          <div className="table-responsive">
            <table className="table table-borderless text-white">
              <thead>
                <tr className="text-muted small">
                  <th>Item</th>
                  <th>Qty</th>
                  <th>Project</th>
                  <th>Returnable</th>
                  <th>Taken Date</th>
                  <th>Return Date</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {history.map((h, idx) => (
                  <tr key={idx}>
                    <td className="fw-semibold">{h.itemName}</td>
                    <td>{h.qty}</td>
                    <td>{h.project}</td>
                    <td>{h.isReturnable ? "Yes" : "No"}</td>
                    <td>{h.takenDate}</td>
                    <td>{h.returnDate || "-"}</td>
                    <td>{h.status}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserHistory;
