import React from "react";

const LowStockCard = ({ containers, onJumpToSlot }) => {
  const lowItems = [];

  containers.forEach((rack) => {
    rack.slots.forEach((slot) => {
      slot.items?.forEach((item) => {
        const takenQty =
          item.taken?.reduce((sum, t) => sum + Number(t.qty), 0) || 0;

        const remaining = item.quantity - takenQty;

        if (remaining < 6) {
          lowItems.push({
            name: item.name,
            remaining,
            rackName: rack.name,
            slotNumber: slot.slotNumber,
            containerId: rack.id,
          });
        }
      });
    });
  });

  return (
    <div
      className="card shadow-sm"
      style={{
        background: "hsl(215, 25%, 12%)",
        border: "1px solid hsl(215, 20%, 25%)",
        color: "white",
      }}
    >
      <div className="card-header fw-bold">Limited Stock Items</div>

      <div
        className="card-body"
        style={{
          maxHeight: "320px",
          overflowY: "auto",
        }}
      >
        {lowItems.length === 0 ? (
          <p className="text-muted">All items have sufficient stock.</p>
        ) : (
          lowItems.map((it, index) => (
            <div
              key={index}
              className="d-flex justify-content-between align-items-center p-3 mb-2 rounded"
              style={{
                background: "hsl(215, 25%, 16%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              {/* LEFT: item name, remaining, rack-slot */}
              <div className="d-flex flex-column">
                <strong>{it.name}</strong>

                <div className="small text-muted">
                  Remaining: {it.remaining}
                </div>

                <div className="small text-muted">
                  {it.rackName} â€¢ Slot {it.slotNumber}
                </div>
              </div>

              {/* RIGHT: Add button */}
              <button
                className="btn btn-primary btn-sm"
                onClick={() => onJumpToSlot(it.containerId, it.slotNumber)}
              >
                + Add
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LowStockCard;
