import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const SlotDetailModal = ({ show, onClose, slot, containerId, onAddItem }) => {
  const [showAddForm, setShowAddForm] = useState(false);

  const items = slot?.items || [];

  const submitAddItem = () => {
    if (!slot?.slotId) return alert("Invalid slot");
    onAddItem(containerId, slot.slotId, {
      itemName: newItem.name.trim(),
      total: Number(newItem.quantity),
    });

    onClose();
  };

  const remainingQty = (it) => Number(it.remaining || it.total || 0);

  return (
    <Modal show={show} onHide={onClose} centered size="lg">
      <Modal.Header closeButton>
        <Modal.Title>Slot {slot?.slotNumber || ""} — Details</Modal.Title>
      </Modal.Header>

      <Modal.Body style={{ backgroundColor: "hsl(215,25%,12%)", color: "white" }}>
        {items.length === 0 && (
          <p className="text-muted">This slot is empty.</p>
        )}

        {items.map((it, idx) => (
          <div
            key={idx}
            className="p-3 mb-3 rounded"
            style={{ backgroundColor: "hsl(215,25%,16%)" }}
          >
            <strong>{it.name || it.itemName}</strong>
            <br />
            <span className="small text-muted">Total: {it.quantity || it.total}</span>
            <br />
            <span className="small">Remaining: {remainingQty(it)}</span>

            {slot?.takenHistory?.length > 0 && (
              <div className="mt-3 small">
                <strong>Taken History:</strong>
                {slot.takenHistory.map((t, index) => (
                  <div key={index}>
                    • {t.userName} — Qty {t.quantity} —
                    {new Date(t.takenDate).toLocaleDateString()}{" "}
                    {t.returnDate ? `(Return: ${new Date(t.returnDate).toLocaleDateString()})` : "(Not returned)"}
                  </div>
                ))}
              </div>
            )}
          </div>
        ))}
      </Modal.Body>

      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>Close</Button>
      </Modal.Footer>
    </Modal>
  );
};

export default SlotDetailModal;
