import React, { useState, useMemo } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

/**
 * SearchBar
 * props:
 *  - containers: array of racks (mockContainers)
 *  - onJump(containerId, slotNumber) -> called when user clicks a result
 *  - onFilterChange(filter) -> called when dropdown filter changes
 *  - setSearchResult(message) -> optional status message setter in parent
 */
const SearchBar = ({ containers = [], onJump, onFilterChange, setSearchResult }) => {
  const [term, setTerm] = useState("");
  const [filter, setFilter] = useState("All"); // All, Returnable, Non-returnable, Occupied, Empty
  const [showResults, setShowResults] = useState(false);

  const matches = useMemo(() => {
    const q = term.trim().toLowerCase();
    if (!q || q.length < 2) return [];
    const out = [];
    containers.forEach((c) => {
      c.slots.forEach((s) => {
        const item = s.item;
        if (item && item.name && item.name.toLowerCase().includes(q)) {
          
          out.push({
            containerId: c.id,
            containerName: c.name,
            slotNumber: s.slotNumber,
            itemName: item.name,
            isReturnable: !!item.isReturnable,
            status: item.status || (item.isReturnable ? "Returnable" : "Non-returnable"),
          });
        }
      });
    });
    return out;
  }, [term, containers]);

  const handleFilter = (value) => {
    setFilter(value);
    if (onFilterChange) onFilterChange(value);
    if (setSearchResult) setSearchResult(`Filter: ${value}`);
    setTimeout(() => setSearchResult && setSearchResult(""), 1500);
  };

  const clear = () => {
    setTerm("");
    setShowResults(false);
    if (setSearchResult) setSearchResult("");
  };

  const onResultClick = (cId, slotNumber) => {
    if (onJump) onJump(cId, slotNumber);
    if (setSearchResult) setSearchResult(`Jumped to ${cId} • Slot ${slotNumber}`);
    setShowResults(false);
    setTimeout(() => setSearchResult && setSearchResult(""), 2000);
  };

  return (
    <div>
      <div className="d-flex gap-2 align-items-center flex-wrap">
        <div style={{ flex: 1 }}>
          <input
            value={term}
            onChange={(e) => {
              setTerm(e.target.value);
              setShowResults(true);
            }}
            className="form-control"
            placeholder="Search items by name (min 2 chars)"
          />
        </div>

        {/* filter dropdown (style 2 as requested) */}
        <div style={{ minWidth: 180 }}>
          <select
            className="form-select"
            value={filter}
            onChange={(e) => handleFilter(e.target.value)}
          >
            <option value="All">All</option>
            <option value="Returnable">Returnable</option>
            <option value="Non-returnable">Non-returnable</option>
            <option value="Occupied">Occupied</option>
            <option value="Empty">Empty</option>
          </select>
        </div>

        <button className="btn btn-outline-secondary" onClick={clear}>
          Reset
        </button>
      </div>

      {/* results dropdown */}
      
{showResults && term.trim().length > 1 && (
  <div
    className="mt-2 p-2 rounded"
    style={{
      backgroundColor: "hsl(215, 25%, 14%)",
      border: "1px solid hsl(215, 20%, 25%)",
      boxShadow: "0 2px 6px rgba(0,0,0,0.25)",
    }}
  >
    {matches.length === 0 ? (
      <div className="small text-muted px-2 py-1">No items found.</div>
    ) : (
      <div>
        {matches.map((m, idx) => (
          <button
            key={`${m.containerId}-${m.slotNumber}-${idx}`}
            type="button"
            className="w-100 text-start border-0 rounded mb-2 p-3"
            onClick={() => onResultClick(m.containerId, m.slotNumber)}
            style={{
              backgroundColor: "hsl(215, 25%, 16%)",
              color: "hsl(210, 40%, 98%)",
              cursor: "pointer",
              transition: "background-color 0.2s ease",
            }}
            onMouseEnter={(e) =>
              (e.currentTarget.style.backgroundColor = "hsl(215, 25%, 22%)")
            }
            onMouseLeave={(e) =>
              (e.currentTarget.style.backgroundColor = "hsl(215, 25%, 16%)")
            }
          >
            <div className="fw-semibold">{m.itemName}</div>
            <div className="small text-secondary">
              {m.containerName} • Slot {m.slotNumber} • {m.status}
            </div>
          </button>
        ))}
      </div>
    )}
  </div>
)}

    </div>
  );
};

export default SearchBar;
