import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import { CircleUserRound } from "lucide-react";

const UserProfile = ({ currentUser, onUpdateUser }) => {
  const [editMode, setEditMode] = useState(false);

  const [form, setForm] = useState({
    name: currentUser?.name || "",
    email: currentUser?.email || "",
    phone: currentUser?.phone || "",
    role: currentUser?.role || "User",
  });

  const handleSave = () => {
    if (!form.name || !form.email || !form.phone) {
      alert("Please fill all fields.");
      return;
    }
    onUpdateUser(form);
    setEditMode(false);
  };

  return (
    <div className="container my-4">
      <div
        className="card d-flex flex-row align-items-center p-4"
        style={{
          maxWidth: 720,
          minHeight: 150,
          background: "hsl(215, 25%, 12%)",
          color: "white",
          borderRadius: 14,
          border: "1px solid hsl(215, 20%, 25%)",
          boxShadow: "0 8px 32px rgba(0,0,0,0.65)",
        }}
      >
        {/* Avatar + Info */}
        <div
          className="d-flex flex-column align-items-center justify-content-center text-center me-4"
          style={{ width: 130 }}
        >
          <div
            className="rounded-circle d-flex justify-content-center align-items-center mb-2"
            style={{
              width: 66,
              height: 66,
              background: "hsl(215, 25%, 16%)",
              border: "2px solid hsl(215, 20%, 25%)",
            }}
          >
            <CircleUserRound size={36} />
          </div>
          <h5 className="fw-semibold mb-0">{form.name || "Your Name"}</h5>
          <small style={{ color: "hsl(215, 15%, 75%)" }}>{form.role}</small>
        </div>

        {/* Form Fields */}
        <Form className="flex-grow-1">
          <div className="row">
            <div className="col-12 col-md-6 mb-2">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={form.name}
                disabled={!editMode}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                style={{
                  backgroundColor: "hsl(215, 25%, 10%)",
                  color: "white",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              />
            </div>
            <div className="col-12 col-md-6 mb-2">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={form.email}
                disabled={!editMode}
                onChange={(e) => setForm({ ...form, email: e.target.value })}
                style={{
                  backgroundColor: "hsl(215, 25%, 10%)",
                  color: "white",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              />
            </div>
            <div className="col-12 col-md-6 mb-2">
              <Form.Label>Phone</Form.Label>
              <Form.Control
                type="text"
                value={form.phone}
                disabled={!editMode}
                onChange={(e) => setForm({ ...form, phone: e.target.value })}
                style={{
                  backgroundColor: "hsl(215, 25%, 10%)",
                  color: "white",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              />
            </div>
            <div className="col-12 col-md-6 mb-2">
              <Form.Label>Role</Form.Label>
              <Form.Control
                type="text"
                value={form.role}
                disabled
                style={{
                  backgroundColor: "hsl(215, 25%, 10%)",
                  color: "white",
                  border: "1px solid hsl(215, 20%, 25%)",
                  opacity: 0.7,
                }}
              />
            </div>
          </div>
        </Form>

        {/* Buttons */}
        <div className="d-flex flex-column gap-2 ms-3">
          {!editMode ? (
            <Button variant="primary" onClick={() => setEditMode(true)} style={{ minWidth: 110 }}>
              Edit
            </Button>
          ) : (
            <>
              <Button
                variant="secondary"
                onClick={() => setEditMode(false)}
                style={{ minWidth: 110 }}
              >
                Cancel
              </Button>
              <Button variant="success" onClick={handleSave} style={{ minWidth: 110 }}>
                Save
              </Button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
