import React from "react";
import { Card, Badge, Row, Col } from "react-bootstrap";

const ContainerDetails = () => {
  const containerName = "Container A";
  const totalSlots = 12;

  const slots = [
    { id: 1, status: "Empty" },
    { id: 2, status: "Empty" },
    {
      id: 3,
      status: "Occupied",
      itemName: "Laptop Dell XPS",
      user: "John Doe",
      returnDate: "2025-01-20",
    },
    { id: 4, status: "Empty" },
    { id: 5, status: "Empty" },
    {
      id: 6,
      status: "Non-Returnable",
      itemName: "iPad Pro",
      user: "Jane Smith",
      note: "This item is not returnable",
    },
    { id: 7, status: "Empty" },
    { id: 8, status: "Empty" },
    { id: 9, status: "Empty" },
    { id: 10, status: "Empty" },
    { id: 11, status: "Empty" },
    { id: 12, status: "Empty" },
  ];
 console
  const occupiedCount = slots.filter(
    (slot) => slot.status !== "Empty"
  ).length;

  return (
    <div className="container py-4">
      <h4 className="text-light fw-bold mb-2">{containerName}</h4>
      <p className="text-muted mb-4">
        {occupiedCount} of {totalSlots} slots occupied
      </p>

      <Row xs={1} sm={2} md={3} lg={4} className="g-3">
        {slots.map((slot) => (
          <Col key={slot.id}>
            <Card
              bg="dark"
              text="light"
              className={`border ${
                slot.status === "Occupied"
                  ? "border-success"
                  : slot.status === "Non-Returnable"
                  ? "border-warning"
                  : "border-secondary"
              } shadow-sm`}
            >
              <Card.Body>
                <div className="d-flex justify-content-between align-items-center mb-2">
                  <div className="d-flex align-items-center gap-1">
                    <i className="bi bi-box text-info"></i>
                    <small className="text-muted">
                      Slot {slot.id}
                    </small>
                  </div>
                  {slot.status === "Occupied" && (
                    <Badge bg="success">Occupied</Badge>
                  )}
                  {slot.status === "Non-Returnable" && (
                    <Badge bg="warning" text="dark">
                      Non-Returnable
                    </Badge>
                  )}
                  {slot.status === "Empty" && (
                    <Badge bg="secondary">Empty</Badge>
                  )}
                </div>

                {slot.status === "Empty" ? (
                  <div>
                    <small className="text-muted">Available slot</small>
                  </div>
                ) : (
                  <div>
                    <h6 className="fw-semibold text-light">
                      {slot.itemName}
                    </h6>
                    <p className="mb-1">
                      <i className="bi bi-person"></i> {slot.user}
                    </p>
                    {slot.status === "Occupied" && (
                      <p className="mb-0">
                        <i className="bi bi-calendar"></i> Return:{" "}
                        {slot.returnDate}
                      </p>
                    )}
                    {slot.status === "Non-Returnable" && (
                      <small className="text-warning">
                        {slot.note}
                      </small>
                    )}
                  </div>
                )}
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
    </div>
  );
};

export default ContainerDetails;
