import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Outlet } from "react-router-dom";
import Navbar from "./Navbar"; // Your converted Bootstrap Navbar
import AppSidebar from "./AppSidebar"; // Your converted Bootstrap Sidebar
import { mockAdmin } from "./mockData"; // Mock admin data

const AdminDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(true);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  return (
    <div className="d-flex bg-light" style={{ minHeight: "100vh", overflow: "hidden" }}>
      {/* Sidebar */}
      <AppSidebar isOpen={sidebarOpen} onClose={toggleSidebar} />

      {/* Main Content Area */}
      <div
        className="d-flex flex-column flex-grow-1"
        style={{
          marginLeft: sidebarOpen ? "240px" : "70px",
          transition: "margin-left 0.3s ease",
          minWidth: 0,
        }}
      >
        {/* Navbar */}
        <Navbar
          admin={mockAdmin}
          onMenuClick={toggleSidebar}
          sidebarOpen={sidebarOpen}
        />

        {/* Page Content */}
        <main
          className="flex-grow-1 p-4"
          style={{
            overflowY: "auto",
            backgroundColor: "#f8f9fa",
          }}
        >
          <Outlet />
        </main>
      </div>
    </div>
  );
};

export default AdminDashboard;
