import React from "react";
import { Nav } from "react-bootstrap";
import { Link, useLocation } from "react-router-dom";
import {
  LayoutDashboard,
  Package,
  History,
  Users,
  ChevronLeft,
} from "lucide-react";

const navigationItems = [
  { title: "Dashboard", url: "/admin/home", icon: LayoutDashboard },
  { title: "Containers", url: "/admin/containers", icon: Package },
  { title: "Users", url: "/admin/users", icon: Users },
  { title: "History", url: "/admin/history", icon: History },
];

const Sidebar = ({ collapsed, toggleSidebar }) => {
  const loc = useLocation();

  return (
    <div
      className="bg-dark text-white d-flex flex-column position-fixed"
      style={{
        width: collapsed ? 80 : 250,
        minHeight: "100vh",
        boxShadow: "0 4px 24px #191e32",
        transition: "width 0.3s ease",
        overflowX: "hidden",
        zIndex: 1000,
      }}
    >
      {/* Header */}
      <div className="d-flex justify-content-between align-items-center px-3 py-3 border-bottom border-secondary">
        {!collapsed && (
          <div className="fw-bold text-uppercase small">
            GANDEEVAN TECHNOLOGIES
          </div>
        )}
        <button
          onClick={toggleSidebar}
          className="btn btn-sm btn-outline-light d-flex align-items-center justify-content-center"
          style={{
            width: 28,
            height: 28,
            borderRadius: "50%",
            padding: 0,
          }}
          title={collapsed ? "Expand Sidebar" : "Collapse Sidebar"}
        >
          <ChevronLeft
            size={16}
            className={`transition-transform ${
              collapsed ? "rotate-180" : ""
            }`}
          />
        </button>
      </div>

      {/* Navigation Links */}
      <Nav className="flex-column py-3 gap-1">
        {navigationItems.map((item) => {
          const isActive = loc.pathname.startsWith(item.url);
          const Icon = item.icon;
          return (
            <Nav.Link
              key={item.title}
              as={Link}
              to={item.url}
              active={isActive}
              title={collapsed ? item.title : ""}
              className={`d-flex align-items-center gap-3 text-white px-3 py-2 rounded ${
                isActive ? "bg-primary text-white fw-semibold" : "text-white"
              }`}
              style={{
                fontSize: collapsed ? "0.9rem" : "1rem",
                justifyContent: collapsed ? "center" : "flex-start",
                transition: "all 0.3s ease",
              }}
            >
              <Icon size={20} className="flex-shrink-0" />
              {!collapsed && <span>{item.title}</span>}
            </Nav.Link>
          );
        })}
      </Nav>
    </div>
  );
};

export default Sidebar;
