// Slot Detail + Add Item Modal + Return Item
// Shows ALL items in a slot and allows returning items that were taken.

import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import api from "../../../api/axios";

const SlotDetailModal = ({
  show,
  onClose,
  slot,
  containerId,
  onAddItem,
}) => {
  const [showAddForm, setShowAddForm] = useState(false);

  const [newItem, setNewItem] = useState({
    name: "",
    quantity: 1,
  });

  // Return-related state
  const [showReturnModal, setShowReturnModal] = useState(false);
  const [returnContext, setReturnContext] = useState(null); // { item, history }
  const [returnQty, setReturnQty] = useState(1);

  // reset when modal closes
  useEffect(() => {
    if (!show) {
      setShowAddForm(false);
      setNewItem({ name: "", quantity: 1 });
      setShowReturnModal(false);
      setReturnContext(null);
      setReturnQty(1);
    }
  }, [show]);

  if (!slot) return null;

  // support both latest (items[]) and old (item)
  const items =
    Array.isArray(slot.items) && slot.items.length > 0
      ? slot.items
      : slot.item
      ? [
          {
            id: slot.item.itemId,
            name: slot.item.itemName,
            quantity: slot.item.total,
            remaining: slot.item.remaining,
            takenHistory: slot.item.takenHistory || [],
          },
        ]
      : [];

  const remainingQty = (it) =>
    Number(
      it.remaining !== undefined && it.remaining !== null
        ? it.remaining
        : it.quantity !== undefined
        ? it.quantity
        : it.total !== undefined
        ? it.total
        : 0
    );

  const totalQty = (it) =>
    Number(
      it.quantity !== undefined
        ? it.quantity
        : it.total !== undefined
        ? it.total
        : remainingQty(it)
    );

  const submitAddItem = () => {
    if (!newItem.name.trim()) return;
    if (!slot.slotId) return;

    onAddItem(containerId, slot.slotId, {
      name: newItem.name.trim(),
      quantity: Number(newItem.quantity),
    });

    setShowAddForm(false);
    onClose();
  };

  // Open Return modal
  const openReturnModal = (item) => {
    if (!item.takenHistory || item.takenHistory.length === 0) {
      alert("No borrow history found for this item.");
      return;
    }

    // pick the latest record (last entry)
    const latest = [...item.takenHistory].slice(-1)[0];

    setReturnContext({ item, history: latest });
    setReturnQty(latest.quantity || 1);
    setShowReturnModal(true);
  };

  // Confirm Return
  const confirmReturn = async () => {
  if (!returnContext) return;

  const { item, history, returnedBy } = returnContext;
  const qty = Number(returnQty);

  if (!returnedBy) {
    alert("Enter the name of the person returning this item.");
    return;
  }

  if (returnedBy.toLowerCase() !== history.userName.toLowerCase()) {
    alert("Returner name does not match who took this item!");
    return;
  }

  if (qty < 1 || qty > (history.quantity || 1)) {
    alert(`Enter valid quantity (1 .. ${history.quantity || 1})`);
    return;
  }

  try {
    await api.post(`/manager/returnItem/${history.borrowId}`, {
      quantity: qty,
    });

    alert("Item returned successfully!");

    setShowReturnModal(false);
    setReturnContext(null);
    setReturnQty(1);
    onClose();

  } catch (err) {
    console.error("Return failed:", err);
    alert("Failed to return item. Check console/logs.");
  }
};


  return (
    <>
      {/* MAIN SLOT DETAIL MODAL */}
      <Modal show={show} onHide={onClose} centered size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            Slot {slot.slotNumber}{" "}
            <strong className="text-muted">-Details</strong>
          </Modal.Title>
        </Modal.Header>

        <Modal.Body
          style={{
            backgroundColor: "hsl(215, 25%, 12%)",
            color: "white",
          }}
        >
          {items.length === 0 && !showAddForm && (
            <p className="text-muted">This slot is empty.</p>
          )}

          {/* Items List */}
          {!showAddForm &&
            items.map((it) => (
              <div
                key={it.id || it.itemId}
                className="p-3 mb-3 rounded"
                style={{
                  backgroundColor: "hsl(215, 25%, 16%)",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              >
                <div className="d-flex justify-content-between">
                  <div>
                    <strong>{it.name || it.itemName}</strong>
                    <div className="small text-muted">
                      Total: {totalQty(it)}
                    </div>
                  </div>

                  <div className="text-end small">
                    <div>Remaining: {remainingQty(it)}</div>
                  </div>
                </div>

                {/* Taken history block
                {slot.takenHistory?.length > 0 && (
                <div className="mt-3 small">
                <strong>Taken History:</strong>
                     {[...slot.takenHistory]
                             .slice(-10)
                             .reverse()
                             .map((t, idx) => (
                   <div key={idx} className="text-muted">
                     • {t.userName} — Qty {t.quantity} —{" "}
                       {new Date(t.takenDate).toLocaleDateString("en-GB")}{" "}
                      {t.returnDate
                          ? `→ Return: ${new Date(t.returnDate).toLocaleDateString("en-GB")}`
                          : "(Not returned)"}
                    </div>
                  ))}
               </div>
                )} */}  

                {/* Taken history block (item-specific) */}
{item.takenHistory?.length > 0 && (
  <div className="mt-3 small">
    <strong>Taken History:</strong>

    {[...item.takenHistory]
      .slice(-10)
      .reverse()
      .map((t, idx) => (
        <div key={idx} className="text-muted">
          • {t.userName} — Qty {t.quantity} —{" "}
          {new Date(t.takenDate).toLocaleDateString("en-GB")}{" "}
          {t.returnDate
            ? `→ Return: ${new Date(t.returnDate).toLocaleDateString("en-GB")}`
            : "(Not returned)"}
        </div>
      ))}
  </div>
)}

                

                {/* Return button – show if some quantity is out (remaining < total) */}
                {remainingQty(it) < totalQty(it) && (
                  <div className="d-flex justify-content-end mt-2 gap-2">
                    <Button
                      variant="warning"
                      onClick={() => openReturnModal(it)}
                    >
                      Return
                    </Button>
                  </div>
                )}
              </div>
            ))}

          {/* Add new item form */}
          {showAddForm && (
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Item Name</Form.Label>
                <Form.Control
                  value={newItem.name}
                  onChange={(e) =>
                    setNewItem({ ...newItem, name: e.target.value })
                  }
                  placeholder="Enter item name"
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Quantity</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={newItem.quantity}
                  onChange={(e) =>
                    setNewItem({
                      ...newItem,
                      quantity: Number(e.target.value),
                    })
                  }
                />
              </Form.Group>
            </Form>
          )}
        </Modal.Body>

        <Modal.Footer>
          {!showAddForm && (
            <Button variant="primary" onClick={() => setShowAddForm(true)}>
              + Add Item
            </Button>
          )}

          {showAddForm && (
            <>
              <Button
                variant="secondary"
                onClick={() => setShowAddForm(false)}
              >
                Back
              </Button>
              <Button variant="primary" onClick={submitAddItem}>
                Save Item
              </Button>
            </>
          )}

          <Button variant="secondary" onClick={onClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      {/* RETURN ITEM MODAL */}
      <Modal
        show={showReturnModal}
        onHide={() => setShowReturnModal(false)}
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title>Return Item</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          {returnContext && (
            <>
              <p>
                <strong>Item:</strong>{" "}
                {returnContext.item.itemName || returnContext.item.name}
              </p>
              <p>
                <strong>Taken by:</strong> {returnContext.history.userName}
              </p>
              <p>
                <strong>Borrow Qty:</strong>{" "}
                {returnContext.history.quantity}
              </p>

              <Form.Group className="mb-3">
                <Form.Label>Quantity to Return</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  max={returnContext.history.quantity}
                  value={returnQty}
                  onChange={(e) => setReturnQty(Number(e.target.value))}
                />
              </Form.Group>
            </>
          )}
        </Modal.Body>

        <Modal.Footer>
          <Button
            variant="secondary"
            onClick={() => setShowReturnModal(false)}
          >
            Cancel
          </Button>
          <Button variant="success" onClick={confirmReturn}>
            Confirm Return
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default SlotDetailModal;
