// components/admin/ContainerGrid.jsx
import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import SlotCard from "./SlotCard";
import { Modal, Button, Form } from "react-bootstrap";
import { Trash2 } from "lucide-react";

const ContainerGrid = ({
  container,
  activeFilter = "All",
  onAddSlot,
  onDeleteRack,
  onDeleteSlot,
  onAddItem,
  onExternalSlotClick,
}) => {
  const occupiedSlots = container.slots.filter(
    (s) => s.items && s.items.length > 0
  ).length;

  const [selectedSlot, setSelectedSlot] = useState(null);
  const [isDetailModalOpen, setIsDetailModalOpen] = useState(false);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);

  const [newItem, setNewItem] = useState({
    id: "",
    name: "",
    quantity: 1,
    isReturnable: true,
    takenBy: "",
    takenQty: 0,
    takenDate: "",
    returnDate: "",
  });

  const handleSlotClick = (slotNumber) => {
    const slot = container.slots.find((s) => s.slotNumber === slotNumber);
    setSelectedSlot(slotNumber);

    if (slot.items.length > 0) setIsDetailModalOpen(true);
    else setIsAddModalOpen(true);

    if (onExternalSlotClick) onExternalSlotClick(container.id, slotNumber);
  };

  const selectedSlotData =
    container.slots.find((s) => s.slotNumber === selectedSlot) || {
      items: [],
    };

  const remainingFor = (item) => {
    let taken = 0;
    item.taken?.forEach((t) => (taken += t.qty || 0));
    return item.quantity - taken;
  };

  const matchesFilter = (slot) => {
    const it = slot.items[0];
    switch (activeFilter) {
      case "Returnable":
        return it && it.isReturnable;
      case "Non-returnable":
        return it && !it.isReturnable;
      case "Occupied":
        return slot.items.length > 0;
      case "Empty":
        return slot.items.length === 0;
      default:
        return true;
    }
  };

  const submitAddItem = () => {
    const itemObj = {
      id: Date.now().toString(),
      name: newItem.name.trim(),
      quantity: Number(newItem.quantity),
      isReturnable: newItem.isReturnable,
      taken: [],
    };

    if (newItem.takenBy && newItem.takenQty > 0) {
      itemObj.taken.push({
        user: newItem.takenBy,
        qty: newItem.takenQty,
        date: newItem.takenDate,
        returnDate: newItem.returnDate || null,
      });
    }

    onAddItem(container.id, selectedSlot, itemObj);

    setNewItem({
      id: "",
      name: "",
      quantity: 1,
      isReturnable: true,
      takenBy: "",
      takenQty: 0,
      takenDate: "",
      returnDate: "",
    });

    setIsAddModalOpen(false);
    setIsDetailModalOpen(false);
  };

  return (
    <>
      {/* Rack Section */}
      <div
        className="card shadow-sm my-3"
        style={{
          background:
            "linear-gradient(90deg, hsl(215, 25%, 12%) 0%, hsl(215, 25%, 10%) 100%)",
          color: "white",
          borderRadius: "10px",
        }}
      >
        <div className="card-header d-flex justify-content-between">
          <h5>{container.name}</h5>
          <div className="d-flex gap-2">
            <button
              className="btn btn-sm btn-outline-light"
              onClick={() => onAddSlot(container.id)}
            >
              +Slot
            </button>

            <button
              className="btn btn-sm btn-secondary"
              onClick={() => onDeleteRack(container.id)}
            >
              <Trash2 size={14} />
            </button>
          </div>
        </div>

        <div className="card-body">
          <div className="row g-3">
            {container.slots.map((slot) => (
              <div
                key={slot.slotNumber}
                className="col-12 col-sm-6 col-md-4 col-lg-3"
                style={{ cursor: "pointer", opacity: matchesFilter(slot) ? 1 : 0.35 }}
                onClick={() => handleSlotClick(slot.slotNumber)}
              >
                <SlotCard
                  slot={slot}
                  containerName={container.name}
                  onDeleteSlot={(slotNum) =>
                    onDeleteSlot(container.id, slotNum)
                  }
                  onAddItemButton={() => {
                    setSelectedSlot(slot.slotNumber);
                    setIsAddModalOpen(true);
                  }}
                />
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Slot Details Modal */}
      <Modal show={isDetailModalOpen} onHide={() => setIsDetailModalOpen(false)} centered size="md">
        <Modal.Header closeButton>
          <Modal.Title>Slot {selectedSlot} - Details</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          {selectedSlotData.items.length === 0 ? (
            <p>No items in this slot.</p>
          ) : (
            selectedSlotData.items.map((it) => (
              <div key={it.id} className="p-2 mb-3 rounded bg-dark text-light">
                <div className="d-flex justify-content-between">
                  <div>
                    <strong>{it.name}</strong>
                    <div className="text-muted small">Total: {it.quantity}</div>
                  </div>
                  <div className="text-end small">
                    {it.isReturnable ? "Returnable" : "Non-returnable"}
                    <div className="text-info">Remaining: {remainingFor(it)}</div>
                  </div>
                </div>

                {it.taken?.length > 0 && (
                  <div className="mt-2 small text-muted">
                    <strong>Taken history:</strong>
                    {it.taken.map((t, i) => (
                      <div key={i}>
                        {t.user} — Qty {t.qty} — {t.date} —{" "}
                        {t.returnDate ? `Return ${t.returnDate}` : "No return"}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            ))
          )}

          <div className="d-flex justify-content-end">
            <Button
              variant="outline-primary"
              onClick={() => {
                setIsDetailModalOpen(false);
                setIsAddModalOpen(true);
              }}
            >
              + Add Item
            </Button>
          </div>
        </Modal.Body>
      </Modal>

      {/* Add Item Modal */}
      <Modal show={isAddModalOpen} onHide={() => setIsAddModalOpen(false)} centered size="md">
        <Modal.Header closeButton>
          <Modal.Title>Add Item to Slot {selectedSlot}</Modal.Title>
        </Modal.Header>

        <Modal.Body>
          <Form>
            <Form.Group className="mb-3">
              <Form.Label>Item Name</Form.Label>
              <Form.Control
                value={newItem.name}
                onChange={(e) => setNewItem({ ...newItem, name: e.target.value })}
              />
            </Form.Group>

            <Form.Group className="mb-3">
              <Form.Label>Quantity</Form.Label>
              <Form.Control
                type="number"
                min="1"
                value={newItem.quantity}
                onChange={(e) =>
                  setNewItem({ ...newItem, quantity: Number(e.target.value) })
                }
              />
            </Form.Group>

            <Form.Group>
              <Form.Check
                type="switch"
                label={newItem.isReturnable ? "Returnable" : "Non-returnable"}
                checked={newItem.isReturnable}
                onChange={(e) =>
                  setNewItem({ ...newItem, isReturnable: e.target.checked })
                }
              />
            </Form.Group>

            {/* Show taken fields only for occupied slot */}
            {selectedSlotData.items.length > 0 && (
              <>
                <hr />
                <div className="text-muted small mb-2">
                  Optional — for taking some items:
                </div>

                <Form.Group className="mb-3">
                  <Form.Label>Taken by</Form.Label>
                  <Form.Control
                    value={newItem.takenBy}
                    onChange={(e) =>
                      setNewItem({ ...newItem, takenBy: e.target.value })
                    }
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Taken Qty</Form.Label>
                  <Form.Control
                    type="number"
                    min="0"
                    value={newItem.takenQty}
                    onChange={(e) =>
                      setNewItem({
                        ...newItem,
                        takenQty: Number(e.target.value),
                      })
                    }
                  />
                </Form.Group>

                <Form.Group className="mb-3">
                  <Form.Label>Taken Date</Form.Label>
                  <Form.Control
                    type="date"
                    value={newItem.takenDate}
                    onChange={(e) =>
                      setNewItem({ ...newItem, takenDate: e.target.value })
                    }
                  />
                </Form.Group>

                {newItem.isReturnable && (
                  <Form.Group className="mb-3">
                    <Form.Label>Return Date</Form.Label>
                    <Form.Control
                      type="date"
                      value={newItem.returnDate}
                      onChange={(e) =>
                        setNewItem({ ...newItem, returnDate: e.target.value })
                      }
                    />
                  </Form.Group>
                )}
              </>
            )}
          </Form>
        </Modal.Body>

        <Modal.Footer>
          <Button variant="secondary" onClick={() => setIsAddModalOpen(false)}>
            Cancel
          </Button>
          <Button variant="primary" onClick={submitAddItem}>
            Add Item
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default ContainerGrid;
