import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";

const PermissionRequests = ({ permissionRequests, onApprove, onReject }) => {
  return (
    <div
      className="card shadow-sm mb-4"
      style={{
        background: "linear-gradient(90deg, hsl(215, 25%, 12%) 0%, hsl(215, 25%, 10%) 100%)",
        color: "hsl(210, 40%, 98%)",
        border: "1px solid hsl(215, 20%, 25%)",
      }}
    >
      <div
        className="card-header"
        style={{
          backgroundColor: "hsl(215, 25%, 12%)",
          borderBottom: "1px solid hsl(215, 20%, 25%)",
        }}
      >
        <h5 className="mb-0 text-light fw-semibold">Permission Requests</h5>
      </div>

      <div className="card-body">
        {permissionRequests.length === 0 ? (
          <p className="text-muted text-center mb-0">
            No permission requests
          </p>
        ) : (
          permissionRequests.map((req) => (
            <div
              key={req.id}
              className="p-3 mb-3 rounded"
              style={{
                backgroundColor: "hsl(215, 25%, 14%)",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            >
              <h6 className="fw-bold mb-1">{req.itemName}</h6>
              <p className="text-muted small mb-1">
                {req.quantity} qty â€¢ {req.itemType}
              </p>

              <p className="small mb-1">
                <strong>User:</strong> {req.userName} ({req.userEmail})
              </p>

              <p className="small mb-2">
                <strong>Requested:</strong> {req.dateRequested}
              </p>

              {req.returnDate && (
                <p className="small text-info mb-2">
                  Return Before: {req.returnDate}
                </p>
              )}

              <div className="d-flex gap-2">
                <button
                  className="btn btn-success btn-sm"
                  onClick={() => onApprove(req.id)}
                >
                  Approve
                </button>
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => onReject(req.id)}
                >
                  Reject
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default PermissionRequests;
