// Slot Detail + Add Item Modal + Return Item (Multi-user Return Supported)

import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import api from "../../../api/axios";

const SlotDetailModal = ({
  show,
  onClose,
  slot,
  containerId,
  onAddItem,
}) => {
  const [showAddForm, setShowAddForm] = useState(false);

  const [newItem, setNewItem] = useState({
    name: "",
    quantity: 1,
  });

  const [showReturnModal, setShowReturnModal] = useState(false);
  const [returnContext, setReturnContext] = useState(null);
  const [returnQty, setReturnQty] = useState(1);

  useEffect(() => {
    if (!show) {
      setShowAddForm(false);
      setShowReturnModal(false);
      setReturnContext(null);
      setNewItem({ name: "", quantity: 1 });
      setReturnQty(1);
    }
  }, [show]);

  if (!slot) return null;

  const items = Array.isArray(slot.items) ? slot.items : [];

  const remainingQty = (it) => Number(it.remaining || it.quantity || it.total || 0);
  const totalQty = (it) => Number(it.total || it.quantity || it.remaining);

  const submitAddItem = () => {
    if (!newItem.name.trim()) return;
    if (!slot.slotId) return;

    onAddItem(containerId, slot.slotId, {
      name: newItem.name.trim(),
      quantity: Number(newItem.quantity),
    });

    setShowAddForm(false);
    onClose();
  };

  const openReturnModal = (item, history) => {
    setReturnContext({ item, history });
    setReturnQty(history.quantity || 1);
    setShowReturnModal(true);
  };

  const confirmReturn = async () => {
    if (!returnContext) return;

    const { history } = returnContext;
    const qty = Number(returnQty);

    if (qty < 1 || qty > (history.quantity || 1)) {
      alert(`Enter valid quantity (1 .. ${history.quantity})`);
      return;
    }

    try {
      await api.post(`/manager/returnItem/${history.borrowId}`, {
        quantity: qty,
      });

      alert("Item returned successfully!");
      setShowReturnModal(false);
      setReturnContext(null);
      onClose();

    } catch (err) {
      console.error("Return failed:", err);
      alert("Failed to return item. Check logs.");
    }
  };

  return (
    <>
      <Modal show={show} onHide={onClose} centered size="lg">
        <Modal.Header closeButton>
          <Modal.Title>
            Slot {slot.slotNumber} <span className="text-muted">- Details</span>
          </Modal.Title>
        </Modal.Header>

        <Modal.Body style={{ backgroundColor: "hsl(215,25%,12%)", color: "white" }}>
          {!showAddForm && items.length === 0 && (
            <p className="text-muted">This slot is empty.</p>
          )}

          {!showAddForm &&
            items.map((it) => (
              <div
                key={it.itemId}
                className="p-3 mb-3 rounded"
                style={{
                  backgroundColor: "hsl(215,25%,16%)",
                  border: "1px solid hsl(215,20%,25%)",
                }}
              >
                <div className="d-flex justify-content-between align-items-center">
                  <div>
                    <strong>{it.itemName}</strong>
                    <div className="small text-muted">Total: {totalQty(it)}</div>
                  </div>
                  <div className="text-end small">
                    Remaining: {remainingQty(it)}
                  </div>
                </div>

                {it.takenHistory?.length > 0 && (
                  <div className="mt-3 small">
                    <strong>Taken history:</strong>
                    {[...it.takenHistory]
                      .slice(-10)
                      .reverse()
                      .map((t, idx) => (
                        <div
                          key={idx}
                          className="text-muted d-flex justify-content-between align-items-center mt-1"
                        >
                          <span>
                            • {t.userName} — Qty {t.quantity} —{" "}
                            {new Date(t.takenDate).toLocaleDateString("en-GB")}{" "}
                            {!t.returnDate
                              ? "(Not returned)"
                              : `→ Return: ${new Date(t.returnDate).toLocaleDateString("en-GB")}`}
                          </span>

                          {/* Show Return button ONLY if not returned */}
                          {!t.returnDate && (
                            <Button
                              variant="warning"
                              size="sm"
                              onClick={() => openReturnModal(it, t)}
                            >
                              Return
                            </Button>
                          )}
                        </div>
                      ))}
                  </div>
                )}
              </div>
            ))}

          {showAddForm && (
            <Form>
              <Form.Group className="mb-3">
                <Form.Label>Item Name</Form.Label>
                <Form.Control
                  value={newItem.name}
                  onChange={(e) =>
                    setNewItem({ ...newItem, name: e.target.value })
                  }
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Quantity</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  value={newItem.quantity}
                  onChange={(e) =>
                    setNewItem({ ...newItem, quantity: Number(e.target.value) })
                  }
                />
              </Form.Group>
            </Form>
          )}
        </Modal.Body>

        <Modal.Footer>
          {!showAddForm && (
            <Button variant="primary" onClick={() => setShowAddForm(true)}>
              + Add Item
            </Button>
          )}

          {showAddForm && (
            <>
              <Button variant="secondary" onClick={() => setShowAddForm(false)}>
                Back
              </Button>
              <Button variant="primary" onClick={submitAddItem}>
                Save Item
              </Button>
            </>
          )}

          <Button variant="secondary" onClick={onClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      {/* RETURN ITEM MODAL */}
      <Modal show={showReturnModal} onHide={() => setShowReturnModal(false)} centered>
        <Modal.Header closeButton>
          <Modal.Title>Return Item</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {returnContext && (
            <>
              <p><strong>User:</strong> {returnContext.history.userName}</p>
              <p><strong>Borrowed Qty:</strong> {returnContext.history.quantity}</p>

              <Form.Group className="mb-3">
                <Form.Label>Quantity to Return</Form.Label>
                <Form.Control
                  type="number"
                  min="1"
                  max={returnContext.history.quantity}
                  value={returnQty}
                  onChange={(e) => setReturnQty(Number(e.target.value))}
                />
              </Form.Group>
            </>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowReturnModal(false)}>
            Cancel
          </Button>
          <Button variant="success" onClick={confirmReturn}>
            Confirm Return
          </Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};

export default SlotDetailModal;
