import React, { useState } from "react";
import { Nav } from "react-bootstrap";
import { Link, useLocation } from "react-router-dom";

const Sidebar = ({collapsed}) => {
  

  return (
    <div
      className={`sidebar bg-dark text-white d-flex flex-column justify-content-between position-fixed`}
      style={{
        width: collapsed ? 80 : 330,
        minHeight: "100vh",
        boxShadow: "0 4px 24px #191e32",
        transition: "width 0.3s",
        overflowX: "hidden",
      }}
    >
      <div>
        <div className="d-flex justify-content-between align-items-center px-3 py-2">
          {!collapsed && <div className="h5 mb-0">GANDEEVAN TECHNOLOGIES</div>}
          <button
            onClick={toggleSidebar}
            className="btn btn-sm btn-outline-light"
            aria-label="Toggle Sidebar"
          >
            {collapsed ? ">" : "<"}
          </button>
        </div>
        <hr class="border-bottom border-white w-10"/>
        <Nav className="flex-column gap-1 px-2">
          <Nav.Link
            as={Link}
            to="/admin/home"
            active={loc.pathname === "/admin/home"}
            className="text-white nav-link-custom"
            title="Dashboard"
          >
            {!collapsed ? "Dashboard" : "D"}
          </Nav.Link>
          
          <Nav.Link
            as={Link}
            to="/admin/containers"
            active={loc.pathname.startsWith("/admin/containers")}
            className="text-white nav-link-custom"
            title="Containers"
          >
            {!collapsed ? "Containers" : "C"}
          </Nav.Link>
          <Nav.Link
            as={Link}
            to="/admin/users"
            active={loc.pathname === "/admin/users"}
            className="text-white nav-link-custom"
            title="User Requests"
          >
            {!collapsed ? "User Requests" : "U"}
          </Nav.Link>
          <Nav.Link
            as={Link}
            to="/admin/history"
            active={loc.pathname === "/admin/history"}
            className="text-white nav-link-custom"
            title="History"
          >
            {!collapsed ? "History" : "H"}
          </Nav.Link>
        </Nav>
      </div>
      
    </div>
  );
};

export default Sidebar;
