import axios from "axios";

const api = axios.create({
  baseURL: "http://localhost:8000/api", // Ensure this matches your backend URL
});

api.interceptors.request.use((config) => {
  let token = localStorage.getItem("token");

  if (token) {
    // **CRITICAL FIX:** Clean up the token by removing any surrounding quotes 
    // that might have been accidentally saved from the login response.
    if (token.startsWith('"') && token.endsWith('"')) {
      token = token.slice(1, -1);
    }

    // Attach the token in the required 'Bearer <token>' format
    config.headers.Authorization = `Bearer ${token}`;
    
    // Console logs for debugging (you can remove these later)
    console.log("✅ Token found. Sending Authorization Header:");
    console.log(`   Header Value: ${config.headers.Authorization}`);
  } else {
    console.log("❌ No token found in localStorage.");
  }
  
  return config;
});

export default api;
