// src/pages/users/UserDashboard.jsx
import React, { useState } from "react";
import { Modal, Button, Form } from "react-bootstrap";

const StatusTag = ({ status }) => {
  const base = {
    padding: "4px 8px",
    borderRadius: 6,
    fontSize: 12,
    fontWeight: 600,
  };
  if (status === "Pending") return <span style={{ ...base, background: "hsl(45,100%,65%)", color: "#222" }}>{status}</span>;
  if (status === "Approved") return <span style={{ ...base, background: "hsl(140,40%,30%)", color: "white" }}>{status}</span>;
  return <span style={{ ...base, background: "hsl(10,80%,40%)", color: "white" }}>{status}</span>;
};

const UserDashboard = ({ permissionRequests = [], onCreateRequest, onCancelRequest, currentUser }) => {
  const [showModal, setShowModal] = useState(false);

  const [form, setForm] = useState({
    itemName: "",
    quantity: 1,
    projectName: "",
    returnable: false,
    returnDate: "",
    message: "",
  });

  const userRequests = permissionRequests || [];

  const handleSubmit = (e) => {
    e.preventDefault();
    // basic validation (required fields)
    if (!form.itemName || !form.quantity || !form.projectName) {
      alert("Please fill required fields");
      return;
    }
    onCreateRequest(form);
    setShowModal(false);
    setForm({
      itemName: "",
      quantity: 1,
      projectName: "",
      returnable: false,
      returnDate: "",
      message: "",
    });
  };

  return (
    <div className="container my-4">
      <div className="d-flex justify-content-between align-items-start mb-3">
        <div>
          <p className="text-muted">User Dashboard</p>
          <h2 className="fw-bold mb-1">Welcome, {currentUser?.name || "User"}</h2>
        </div>

        <div>
          <Button onClick={() => setShowModal(true)} className="btn-primary">Request New Item</Button>
        </div>
      </div>

      {/* My Requests */}
      <div className="card p-3 mb-4" style={{ background: "hsl(215,25%,12%)", border: "1px solid hsl(215,20%,25%)", color: "white" }}>
        <h5 className="fw-bold mb-3">My Requests</h5>

        {userRequests.length === 0 ? (
          <p className="text-muted">You have not made any requests yet.</p>
        ) : (
          userRequests.map((req) => (
            <div key={req.id} className="d-flex justify-content-between align-items-start p-3 mb-2" style={{ background: "hsl(215,25%,16%)", border: "1px solid hsl(215,20%,25%)", borderRadius: 6 }}>
              <div>
                <div className="fw-bold" style={{ fontSize: 16 }}>{req.itemName}</div>
                <div className="text-muted">Project: {req.whichProject}</div>
                <div className="text-muted small">Requested: {req.dateRequested}</div>
                {req.itemType === "Returnable" && <div className="text-muted small">Return Date: {req.returnDate}</div>}
                {req.message && <div style={{ marginTop: 6, background: "hsl(215,25%,14%)", padding: 8, borderRadius: 6 }}>{req.message}</div>}
              </div>

              <div className="text-end">
                <div style={{ marginBottom: 8 }}><StatusTag status={req.status} /></div>
                <div>Qty: <strong>{req.quantity}</strong></div>

                {/* Cancel (only when Pending) */}
                {req.status === "Pending" && (
                  <Button variant="danger" size="sm" className="mt-2" onClick={() => onCancelRequest(req.id)}>
                    Cancel Request
                  </Button>
                )}
              </div>
            </div>
          ))
        )}
      </div>

      {/* Request Modal */}
      <Modal show={showModal} onHide={() => setShowModal(false)} centered>
        <Modal.Header closeButton style={{ background: "hsl(215,25%,12%)", color: "white" }}>
          <Modal.Title>Request New Item</Modal.Title>
        </Modal.Header>
        <Modal.Body style={{ background: "hsl(215,25%,14%)", color: "white" }}>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-2">
              <Form.Label>Item Name *</Form.Label>
              <Form.Control value={form.itemName} onChange={(e) => setForm({...form, itemName: e.target.value})} style={{ background: "hsl(215,25%,12%)", color: "white", border: "1px solid hsl(215,20%,25%)" }} />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Quantity *</Form.Label>
              <Form.Control type="number" min={1} value={form.quantity} onChange={(e) => setForm({...form, quantity: e.target.value})} style={{ background: "hsl(215,25%,12%)", color: "white", border: "1px solid hsl(215,20%,25%)" }} />
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Project Name *</Form.Label>
              <Form.Control value={form.projectName} onChange={(e) => setForm({...form, projectName: e.target.value})} style={{ background: "hsl(215,25%,12%)", color: "white", border: "1px solid hsl(215,20%,25%)" }} />
            </Form.Group>

            <Form.Group className="mb-2 d-flex align-items-center">
              <Form.Check type="checkbox" label="Returnable?" checked={form.returnable} onChange={(e) => setForm({...form, returnable: e.target.checked})} />
              {form.returnable && (
                <input type="date" className="form-control ms-2" value={form.returnDate} onChange={(e) => setForm({...form, returnDate: e.target.value})} style={{ maxWidth: 220, background: "hsl(215,25%,12%)", color: "white", border: "1px solid hsl(215,20%,25%)" }} />
              )}
            </Form.Group>

            <Form.Group className="mb-2">
              <Form.Label>Message (optional)</Form.Label>
              <Form.Control as="textarea" rows={3} value={form.message} onChange={(e) => setForm({...form, message: e.target.value})} style={{ background: "hsl(215,25%,12%)", color: "white", border: "1px solid hsl(215,20%,25%)" }} />
            </Form.Group>

            <div className="d-flex gap-2 justify-content-end mt-3">
              <Button variant="secondary" onClick={() => setShowModal(false)}>Close</Button>
              <Button variant="primary" type="submit">Submit Request</Button>
            </div>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default UserDashboard;
