// pages/Containers.jsx
import React, { useState, useRef, useEffect } from "react";
import ContainerGrid from "../components/admin/ContainerGrid";
import { mockContainers as initialMock } from "../data/Mockdata";
import SearchBar from "../components/admin/SearchBar";
import ContainerSummaryCard from "../components/admin/ContainerSummaryCard";

const Containers = () => {
  const [containers, setContainers] = useState(() =>
    initialMock.map((c) => ({
      ...c,
      slots: c.slots.map((s) => ({ ...s, item: s.item ? { ...s.item } : null }))
    }))
  );

  const [activeFilter, setActiveFilter] = useState("All");
  const [searchResult, setSearchResult] = useState("");
  const scrollTimeoutRef = useRef(null);

  const handleAddRack = () => {
    const nextIndex = containers.length + 1;
    const newRackName = `Rack C${nextIndex}`;
    const newId = String(nextIndex);

    const newRack = {
      id: newId,
      name: newRackName,
      slots: [
        { slotNumber: 1, item: null }  // only 1 slot as you requested
      ]
    };

    setContainers((prev) => [...prev, newRack]);
    setSearchResult(`Created ${newRackName}`);
    setTimeout(() => setSearchResult(""), 2000);
  };

  const handleAddSlot = (containerId) => {
    setContainers((prev) =>
      prev.map((c) => {
        if (c.id !== containerId) return c;
        const newSlotNumber = c.slots.length + 1;
        return {
          ...c,
          slots: [...c.slots, { slotNumber: newSlotNumber, item: null }]
        };
      })
    );
    setSearchResult("Slot added");
    setTimeout(() => setSearchResult(""), 1500);
  };

  const jumpToSlot = (containerId, slotNumber) => {
    const id = `rack-${containerId}-slot-${slotNumber}`;
    const el = document.getElementById(id);
    if (!el) return;

    el.scrollIntoView({ behavior: "smooth", block: "center" });
  };

  const onFilterChange = (f) => {
    setActiveFilter(f);
  };

  return (
    <div className="container my-4">

      {/* Header */}
      <div className="mb-4 d-flex justify-content-between">
        <h2 className="fw-bold">Container Management</h2>
        <button className="btn btn-secondary" onClick={handleAddRack}>
          + Add Rack
        </button>
      </div>

      {/* SearchBar */}
      <SearchBar
        containers={containers}
        onJump={jumpToSlot}
        onFilterChange={onFilterChange}
        setSearchResult={setSearchResult}
      />

      {searchResult && (
        <div className="alert alert-info mt-3">{searchResult}</div>
      )}

      {/* ðŸ”¥ Summary Cards â€” now using UPDATED STATE */}
      <div className="row g-3 mt-4">
        {containers.map((container) => (
          <div className="col-12 col-md-6 col-lg-4" key={container.id}>
            <ContainerSummaryCard container={container} />
          </div>
        ))}
      </div>

      {/* Rack Details */}
      <div className="d-flex flex-column gap-4 mt-4">
        {containers.map((container) => (
          <ContainerGrid
            key={container.id}
            container={container}
            activeFilter={activeFilter}
            onAddSlot={handleAddSlot}
          />
        ))}
      </div>

    </div>
  );
};

export default Containers;
