import React from "react";
import { NavLink } from "react-router-dom";
import { LayoutDashboard, Package, History } from "lucide-react";

const items = [
  { title: "Dashboard", url: "/users", icon: LayoutDashboard },
  { title: "Racks", url: "/users/racks", icon: Package },
  { title: "My History", url: "/users/history", icon: History },
];

const UserSidebar = ({ isOpen, onClose }) => {
  return (
    <>
      {isOpen && (
        <div
          className="position-fixed top-0 start-0 w-100 h-100 bg-dark bg-opacity-50 d-lg-none"
          onClick={onClose}
          style={{ zIndex: 1090 }}
        />
      )}

      <aside
        className="position-fixed top-0 start-0 h-100 d-flex flex-column"
        style={{
          width: isOpen ? 240 : 70,
          background: "linear-gradient(180deg, hsl(215,25%,12%), hsl(215,25%,10%))",
          borderRight: "1px solid hsl(215,20%,25%)",
          color: "white",
          zIndex: 1100,
          transition: "width .25s ease",
          paddingTop: 72,
        }}
      >
        <div className="px-3 py-3 d-flex align-items-center justify-content-between">
          {isOpen && <h6 className="text-muted mb-0">User Panel</h6>}
        </div>

        <nav className="px-2 py-2 overflow-auto">
          {items.map((it) => {
            const Icon = it.icon;
            return (
              <NavLink
                key={it.url}
                to={it.url}
                className={({ isActive }) =>
                  `d-flex align-items-center gap-2 py-2 px-3 mb-1 rounded ${isActive ? "bg-primary text-white fw-semibold" : "text-light"}`
                }
              >
                <Icon size={18} />
                <span className="d-none d-md-inline">{it.title}</span>
              </NavLink>
            );
          })}
        </nav>
      </aside>
    </>
  );
};

export default UserSidebar;
