// pages/Containers.jsx
import React, { useState, useRef, useEffect } from "react";
import ContainerGrid from "../components/admin/ContainerGrid";
import { mockContainers as initialMock } from "../data/Mockdata";
import SearchBar from "../components/admin/SearchBar";
import AddRackModal from "../components/admin/AddRackModal"; 

const Containers = () => {
  // local copy of mockContainers so we can add racks/slots
  const [containers, setContainers] = useState(() =>
    initialMock.map((c) => ({
      ...c,
      slots: c.slots.map((s) => ({
        ...s,
        item: s.item ? { ...s.item } : null,
      })),
    }))
  );

  const [activeFilter, setActiveFilter] = useState("All"); //Stores which filter is currently applied: All, Returnable, Non-returnable
  const [searchResult, setSearchResult] = useState("");    //Used to show a temporary message like:
  const scrollTimeoutRef = useRef(null);                   //Used to clear timeout for scroll highlight

  // MODAL state
  const [showRackModal, setShowRackModal] = useState(false); //Controls if the “Add Rack” popup is open

  
  // UPDATED: Add Rack → Ask Name + Create 1 Slot
  
  const handleCreateRack = (rackName) => {
    const newId = (containers.length + 1).toString();  //Generates new rack ID

    const newRack = {
      id: newId,
      name: rackName, // user entered name
      slots: [
        {
          slotNumber: 1,
          item: null, // only ONE default slot
        },
      ],
    };

    setContainers((prev) => [...prev, newRack]); //Add new rack to state
    setSearchResult(`Created ${rackName}`);      // Show temporary message
    setTimeout(() => setSearchResult(""), 2000);  // Clear message after 2s
  };

  // Add slot to a container (UNCHANGED)
  const handleAddSlot = (containerId) => { //Adds a new slot to the specified container
    setContainers((prev) =>
      prev.map((c) => {
        if (c.id !== containerId) return c;
        const newSlotNumber = c.slots.length + 1;
        return {
          ...c,
          slots: [...c.slots, { slotNumber: newSlotNumber, item: null }], //New empty slot
        };
      })
    );
    setSearchResult("Slot added");  // Temporary message
    setTimeout(() => setSearchResult(""), 1500);
  };

  

  // Filter change (UNCHANGED)
  const onFilterChange = (f) => {  //Handles when user changes filter in SearchBar
    setActiveFilter(f);
    setSearchResult(`Filter: ${f}`);
    setTimeout(() => setSearchResult(""), 1400);
  };

  useEffect(() => {                       //Cleans timeout when component unmounts
    return () => {
      if (scrollTimeoutRef.current) clearTimeout(scrollTimeoutRef.current);  //Clear timeout
    };
  }, []);

  return (
    <div className="container my-4">
      {/* Page Header */}
      <div className="mb-4 d-flex justify-content-between align-items-start flex-column flex-md-row gap-3">
        <div>
          <h2 className="fw-bold mb-1">Container Management</h2>
          <p className="text-muted">Manage all your containers and slots efficiently</p>
        </div>

        <div className="d-flex gap-2 align-items-center"> 
          {/* OPEN MODAL */}
          <button className="btn btn-secondary" onClick={() => setShowRackModal(true)}>  
            + Add Rack
          </button>
        </div>
      </div>

      {/* Search + Filter */}
      <div className="mb-4">
        <SearchBar
          containers={containers}
          onFilterChange={onFilterChange}
         setSearchResult={setSearchResult}
        />

        {searchResult && (
          <div className="alert alert-info mt-3" role="alert">
            {searchResult}
          </div>
        )}
      </div>

      {/* Container List */}
      <div className="d-flex flex-column gap-4">
        {containers.map((container) => (
          <ContainerGrid 
            key={container.id}                 //Each container rack
            container={container}              //Container data
            activeFilter={activeFilter}         //Current filter
            onAddSlot={handleAddSlot}         //Function to add slot
            onExternalSlotClick={() => {}}     //No-op for now
          />
        ))}
      </div>

      {/* ADD RACK MODAL */}
      <AddRackModal
        show={showRackModal}                       //Controls if modal is shown
        onClose={() => setShowRackModal(false)}    //
        onCreate={handleCreateRack}
      />
    </div>
  );
};

export default Containers;
