import React, { useEffect, useState } from "react";
import api from "../../../api/axios";
import { Maximize2, X } from "lucide-react";

const ItemDueCard = () => {
  const [rows, setRows] = useState([]);
  const [showFullView, setShowFullView] = useState(false);

  const sendReminder = async (id) => {
    try {
      await api.post(`/manager/sendReminder/${id}`);
      alert("Email Reminder Sent!");
    } catch (err) {
      console.error("Reminder failed", err);
    }
  };

  const fetchDueDates = async () => {
    try {
      const res = await api.get("/manager/dueDates");
      if (res.data && res.data.data) setRows(res.data.data);
    } catch (err) {
      console.error("Error fetching due items:", err);
      setRows([]);
    }
  };

  useEffect(() => {
    fetchDueDates();
  }, []);

  const formatDate = (date) =>
    date ? new Date(date).toLocaleDateString("en-IN") : "-";

  return (
    <>
      {/* SMALL CARD */}
      <div
        className="card shadow-sm"
        style={{
          background: "hsl(215, 25%, 12%)",
          border: "1px solid hsl(215, 20%, 25%)",
          color: "white",
          height: "100%",
        }}
      >
        <div
          className="card-header fw-bold d-flex justify-content-between align-items-center"
          style={{ borderColor: "hsl(215,25%,25%)" }}
        >
          Item Due Dates

          {/* üîç Expand button */}
          <Maximize2
            size={18}
            style={{ cursor: "pointer" }}
            onClick={() => setShowFullView(true)}
          />
        </div>

        {/* Table Container */}
        <div
          className="card-body"
          style={{
            maxHeight: "320px",
            overflowY: "auto",
            overflowX: "auto",
            paddingRight: "6px",
          }}
        >
          <div style={{ borderRadius: 8, border: "1px solid rgba(0,0,0,0.06)" }}>
            <table
              className="table-bordered mb-0"
              style={{ width: "100%", backgroundColor: "#191f24" }}
            >
              <thead
                style={{
                  top: 0,
                  zIndex: 3,
                  background: "hsl(215, 25%, 18%)",
                }}
              >
                <tr>
                  <th>Item</th>
                  <th>User</th>
                  <th>Taken</th>
                  <th>Return</th>
                  <th>Days Left</th>
                  <th>Overdue</th>
                  <th></th>
                </tr>
              </thead>

              <tbody>
                {rows.length === 0 ? (
                  <tr>
                    <td colSpan="7" className="text-center p-2">
                      No due items
                    </td>
                  </tr>
                ) : (
                  rows.map((r, i) => (
                    <tr key={r.borrowId}>
                      <td>{r.itemName}</td>
                      <td>{r.userName}</td>
                      <td>{formatDate(r.takenDate)}</td>
                      <td>{formatDate(r.returnDate)}</td>
                      <td>{r.daysLeft ?? "-"}</td>
                      <td
                        style={{
                          color: r.overdueDays > 0 ? "red" : "white",
                        }}
                      >
                        {r.overdueDays ?? 0} days
                      </td>
                      <td>
                        <button
                          onClick={() => sendReminder(r.borrowId)}
                          className="btn btn-primary btn-sm"
                        >
                          Reminder
                        </button>
                      </td>
                    </tr>
                  ))
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      {/* FULL POPUP VIEW */}
      {showFullView && (
        <div
          className="position-fixed top-0 start-0 w-100 h-100 d-flex justify-content-center align-items-center"
          style={{
            background: "rgba(0,0,0,0.7)",
            zIndex: 9999,
          }}
        >
          <div
            className="card shadow-lg"
            style={{
              width: "90%",
              maxHeight: "90vh",
              background: "hsl(215, 25%, 12%)",
              border: "1px solid hsl(215, 20%, 25%)",
              color: "white",
              overflow: "auto",
            }}
          >
            <div
              className="card-header d-flex justify-content-between align-items-center"
              style={{ borderColor: "hsl(215,25%,25%)" }}
            >
              <strong>Item Due Dates - Full View</strong>

              {/* ‚ùå Close button */}
              <X
                size={20}
                style={{ cursor: "pointer" }}
                onClick={() => setShowFullView(false)}
              />
            </div>

            {/* Reuse same table (copy from above) */}
            <div className="card-body p-0">
              <div style={{ borderRadius: 8 }}>
                <table
                  className="table-bordered mb-0"
                  style={{ width: "100%", backgroundColor: "#191f24" }}
                >
                  <thead
                    style={{
                      background: "hsl(215, 25%, 18%)",
                      position: "sticky",
                      top: 0,
                    }}
                  >
                    <tr>
                      <th>Item</th>
                      <th>User</th>
                      <th>Taken</th>
                      <th>Return</th>
                      <th>Days Left</th>
                      <th>Overdue</th>
                      <th></th>
                    </tr>
                  </thead>

                  <tbody>
                    {rows.map((r, i) => (
                      <tr key={r.borrowId}>
                        <td>{r.itemName}</td>
                        <td>{r.userName}</td>
                        <td>{formatDate(r.takenDate)}</td>
                        <td>{formatDate(r.returnDate)}</td>
                        <td>{r.daysLeft ?? "-"}</td>
                        <td
                          style={{
                            color: r.overdueDays > 0 ? "red" : "white",
                          }}
                        >
                          {r.overdueDays ?? 0} days
                        </td>
                        <td>
                          <button
                            onClick={() => sendReminder(r.borrowId)}
                            className="btn btn-primary btn-sm"
                          >
                            Reminder
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default ItemDueCard;
