import React, { useState, useEffect } from "react";
import { Modal, Button, Form } from "react-bootstrap";
import api from "../../api/axios";
import NotificationToast from "../common/NotificationToast";

const ManagerPermissionModal = ({ show, onClose, request, onGiven }) => {
  const [racks, setRacks] = useState([]);
  const [selectedRackId, setSelectedRackId] = useState("");
  const [selectedSlotId, setSelectedSlotId] = useState("");

  const [toast, setToast] = useState({
    show: false,
    message: "",
    bg: "success",
  });

  useEffect(() => {
    if (show) {
      fetchRacks();
      setSelectedRackId("");
      setSelectedSlotId("");
    }
  }, [show]);

  const fetchRacks = async () => {
    try {
      const res = await api.get("/racks/all");
      setRacks(res.data.data || []);
    } catch (err) {
      console.error("Failed loading racks:", err);
    }
  };

  const selectedRack = racks.find((r) => r.id === selectedRackId);

  const handleGiveItem = async () => {
    if (!selectedRackId || !selectedSlotId) {
      setToast({
        show: true,
        message: "Please select both rack and slot!",
        bg: "danger",
      });
      return;
    }

    try {
      await api.post(`/manager/giveItem/${request._id}`, {
        rackId: selectedRackId,
        slotId: selectedSlotId,
      });

      setToast({
        show: true,
        message: "Item successfully given!",
        bg: "success",
      });

      setTimeout(() => {
        onGiven();
        onClose();
      }, 1000);
    } catch (err) {
      console.error(err);
      setToast({
        show: true,
        message: "Failed to issue item!",
        bg: "danger",
      });
    }
  };

  if (!request) return null;

  return (
    <Modal show={show} onHide={onClose} centered>
      <Modal.Header closeButton className="text-white" style={{ background: "hsl(215,25%,10%)" }}>
        <Modal.Title>Give Item To User</Modal.Title>
      </Modal.Header>

      <Modal.Body className="text-white" style={{ background: "hsl(215,25%,20%)" }}>
        <p><strong>User:</strong> {request.user.name}</p>
        <p><strong>Email:</strong> {request.user.email}</p>
        <hr />
        <p><strong>Item:</strong> {request.itemName}</p>
        <p><strong>Qty:</strong> {request.quantity}</p>

        <hr />
        {/* Rack Dropdown */}
        <Form.Group className="mt-2">
          <Form.Label>Rack</Form.Label>
          <Form.Select
            value={selectedRackId}
            onChange={(e) => setSelectedRackId(e.target.value)}
          >
            <option value="">Select Rack</option>
            {racks.map((rack) => (
              <option key={rack.id} value={rack.id}>
                {rack.name}
              </option>
            ))}
          </Form.Select>
        </Form.Group>

        {/* Slot Dropdown */}
        {selectedRack && (
          <Form.Group className="mt-3">
            <Form.Label>Slot</Form.Label>
            <Form.Select
              value={selectedSlotId}
              onChange={(e) => setSelectedSlotId(e.target.value)}
            >
              <option value="">Select Slot</option>
              {selectedRack.slots.map((slot) => (
                <option key={slot.slotId} value={slot.slotId}>
                  Slot {slot.slotNumber}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
        )}

      </Modal.Body>

      <Modal.Footer className="bg-dark" style={{ borderTop: "1px solid hsl(215,20%,25%)" }}>
        <Button variant="success" onClick={handleGiveItem}>
          Issue Item âœ“
        </Button>
      </Modal.Footer>

      {/* Toast */}
      <NotificationToast
        show={toast.show}
        onClose={() => setToast({ ...toast, show: false })}
        message={toast.message}
        bg={toast.bg}
      />
    </Modal>
  );
};

export default ManagerPermissionModal;
