import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import { CircleUserRound } from "lucide-react"; // avatar-style icon

const UserProfile = ({ currentUser, onUpdateUser }) => {
  const [editMode, setEditMode] = useState(false);

  const [form, setForm] = useState({
    name: currentUser?.name || "",
    email: currentUser?.email || "",
    phone: currentUser?.phone || "",
    role: currentUser?.role || "User",
  });

  const handleSave = () => {
    if (!form.name || !form.email || !form.phone) {
      alert("Please fill all fields.");
      return;
    }
    onUpdateUser(form);
    setEditMode(false);
  };

  return (
    <div className="container my-4">
      <div
        className="card mx-auto p-4"
        style={{
          maxWidth: 420,
          background: "hsl(215, 25%, 12%)",
          color: "white",
          borderRadius: 16,
          boxShadow: "0 8px 40px rgba(0,0,0,0.65)",
          border: "1px solid hsl(215, 20%, 25%)",
        }}
      >
        {/* Avatar & Name */}
        <div className="text-center mb-3">
          <div
            className="rounded-circle d-flex justify-content-center align-items-center mx-auto mb-2"
            style={{
              width: 72,
              height: 72,
              background: "hsl(215, 25%, 16%)",
              border: "2px solid hsl(215, 20%, 25%)",
              boxShadow: "0 0 0 6px rgba(255,255,255,0.04)",
            }}
          >
            <CircleUserRound size={40} />
          </div>
          <h4 className="fw-bold mb-2">{form.name || "Your Name"}</h4>
          <div style={{ fontWeight: 500, color: "hsl(215,15%,75%)", marginBottom: 2 }}>
            {form.role}
          </div>
          <small className="text-muted">{form.email || "you@example.com"}</small>
        </div>

        {/* Info Form */}
        <Form>
          <Form.Group className="mb-3">
            <Form.Label>Name</Form.Label>
            <Form.Control
              disabled={!editMode}
              value={form.name}
              onChange={(e) => setForm({ ...form, name: e.target.value })}
              style={{
                backgroundColor: "hsl(215, 25%, 10%)",
                color: "white",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Email</Form.Label>
            <Form.Control
              disabled={!editMode}
              type="email"
              value={form.email}
              onChange={(e) => setForm({ ...form, email: e.target.value })}
              style={{
                backgroundColor: "hsl(215, 25%, 10%)",
                color: "white",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Phone Number</Form.Label>
            <Form.Control
              disabled={!editMode}
              value={form.phone}
              onChange={(e) => setForm({ ...form, phone: e.target.value })}
              style={{
                backgroundColor: "hsl(215, 25%, 10%)",
                color: "white",
                border: "1px solid hsl(215, 20%, 25%)",
              }}
            />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Role</Form.Label>
            <Form.Control
              disabled
              value={form.role}
              style={{
                backgroundColor: "hsl(215, 25%, 10%)",
                color: "white",
                border: "1px solid hsl(215, 20%, 25%)",
                opacity: 0.7,
              }}
            />
          </Form.Group>
        </Form>

        {/* Buttons */}
        <div className="d-flex gap-2 mt-3 justify-content-end">
          {!editMode ? (
            <Button
              onClick={() => setEditMode(true)}
              variant="primary"
              style={{ minWidth: 120 }}
            >
              Edit Profile
            </Button>
          ) : (
            <>
              <Button
                variant="secondary"
                onClick={() => setEditMode(false)}
                style={{ minWidth: 100 }}
              >
                Cancel
              </Button>
              <Button variant="success" onClick={handleSave} style={{ minWidth: 120 }}>
                Save Changes
              </Button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
