// pages/Containers.jsx
import React, { useState, useRef, useEffect } from "react";
import ContainerGrid from "../components/admin/ContainerGrid";
import { mockContainers as initialMock } from "../data/Mockdata";
import SearchBar from "../components/admin/SearchBar";

const Containers = () => {
  // local copy of mockContainers so we can add racks/slots
  const [containers, setContainers] = useState(() =>
    initialMock.map((c) => ({ ...c, slots: c.slots.map((s) => ({ ...s, item: s.item ? { ...s.item } : null })) }))
  );

  const [activeFilter, setActiveFilter] = useState("All");
  const [searchResult, setSearchResult] = useState("");
  const scrollTimeoutRef = useRef(null);

  // NEW: rack creation modal state
  const [showRackModal, setShowRackModal] = useState(false);
  const [newRackName, setNewRackName] = useState("");

  // Add new rack - now asks for name and creates only 1 slot by default
  const handleAddRackConfirmed = () => {
    const name = newRackName.trim();
    if (!name) {
      setSearchResult("Rack name is required");
      setTimeout(() => setSearchResult(""), 1500);
      return;
    }

    // use timestamp for unique id to avoid duplicates if racks are deleted/renamed
    const newId = Date.now().toString();

    const newRack = {
      id: newId,
      name,
      slots: [
        { slotNumber: 1, item: null } // only 1 slot by default
      ],
    };

    setContainers((prev) => [...prev, newRack]);
    setSearchResult(`Created ${name}`);
    setTimeout(() => setSearchResult(""), 2000);

    setNewRackName("");
    setShowRackModal(false);
  };

  // Add slot to a container
  const handleAddSlot = (containerId) => {
    setContainers((prev) =>
      prev.map((c) => {
        if (c.id !== containerId) return c;
        const newSlotNumber = c.slots.length + 1;
        return {
          ...c,
          slots: [...c.slots, { slotNumber: newSlotNumber, item: null }],
        };
      })
    );
    setSearchResult("Slot added");
    setTimeout(() => setSearchResult(""), 1500);
  };

  // Delete a rack
  const handleDeleteRack = (containerId) => {
    if (!window.confirm("Are you sure you want to delete this rack?")) return;
    setContainers((prev) => prev.filter((c) => c.id !== containerId));
    setSearchResult("Rack deleted");
    setTimeout(() => setSearchResult(""), 1500);
  };

  // Delete a slot in a rack (re-index remaining slots)
  const handleDeleteSlot = (containerId, slotNumber) => {
    if (!window.confirm(`Delete slot ${slotNumber} from this rack?`)) return;
    setContainers((prev) =>
      prev.map((c) => {
        if (c.id !== containerId) return c;
        // remove the slot
        const remaining = c.slots.filter((s) => s.slotNumber !== slotNumber);
        // reindex slotNumber to be contiguous starting from 1
        const reindexed = remaining.map((s, idx) => ({ ...s, slotNumber: idx + 1 }));
        return { ...c, slots: reindexed };
      })
    );
    setSearchResult("Slot deleted");
    setTimeout(() => setSearchResult(""), 1500);
  };

  // Jump to a slot (called from SearchBar)
  const jumpToSlot = (containerId, slotNumber) => {
    const id = `rack-${containerId}-slot-${slotNumber}`;
    const el = document.getElementById(id);
    if (!el) {
      setSearchResult("Slot not visible (maybe filtered)");
      setTimeout(() => setSearchResult(""), 2000);
      return;
    }

    el.scrollIntoView({ behavior: "smooth", block: "center" });

    // flash highlight
    el.style.transition = "box-shadow 0.25s ease, transform 0.15s";
    el.style.boxShadow = "0 0 0 4px rgba(0,123,255,0.35)";
    el.style.transform = "translateY(-4px)";
    if (scrollTimeoutRef.current) clearTimeout(scrollTimeoutRef.current);
    scrollTimeoutRef.current = setTimeout(() => {
      el.style.boxShadow = "";
      el.style.transform = "";
    }, 1400);
  };

  // SearchBar onFilterChange handler
  const onFilterChange = (f) => {
    setActiveFilter(f);
    setSearchResult(`Filter: ${f}`);
    setTimeout(() => setSearchResult(""), 1400);
  };

  // cleanup
  useEffect(() => {
    return () => {
      if (scrollTimeoutRef.current) clearTimeout(scrollTimeoutRef.current);
    };
  }, []);

  return (
    <div className="container my-4">
      {/* Page Header */}
      <div className="mb-4 d-flex justify-content-between align-items-start flex-column flex-md-row gap-3">
        <div>
          <h2 className="fw-bold mb-1">Container Management</h2>
          <p className="text-muted">Manage all your containers and slots efficiently</p>
        </div>

        <div className="d-flex gap-2 align-items-center">
          <button className="btn btn-secondary" onClick={() => setShowRackModal(true)}>
            + Add Rack
          </button>
        </div>
      </div>

      {/* Rack creation modal */}
      {showRackModal && (
        <div
          className="modal d-block"
          tabIndex="-1"
          role="dialog"
          onMouseDown={(e) => {
            // close when clicking outside modal content
            if (e.target.classList.contains("modal")) setShowRackModal(false);
          }}
        >
          <div className="modal-dialog modal-dialog-centered" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Create New Rack</h5>
                <button type="button" className="btn-close" aria-label="Close" onClick={() => setShowRackModal(false)} />
              </div>
              <div className="modal-body">
                <label className="form-label">Rack Name</label>
                <input
                  type="text"
                  className="form-control"
                  placeholder="Rack C4"
                  value={newRackName}
                  onChange={(e) => setNewRackName(e.target.value)}
                />
                <p className="text-muted small mt-2">
                  1 empty slot will be added automatically. You can add more slots later.
                </p>
              </div>
              <div className="modal-footer">
                <button className="btn btn-secondary" onClick={() => setShowRackModal(false)}>
                  Cancel
                </button>
                <button className="btn btn-primary" onClick={handleAddRackConfirmed}>
                  Create Rack
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Search + Filter */}
      <div className="mb-4">
        <SearchBar
          containers={containers}
          onJump={jumpToSlot}
          onFilterChange={onFilterChange}
          setSearchResult={setSearchResult}
        />
        {searchResult && (
          <div className="alert alert-info mt-3" role="alert">
            {searchResult}
          </div>
        )}
      </div>

      {/* Container List */}
      <div className="d-flex flex-column gap-4">
        {containers.map((container) => (
          <ContainerGrid
            key={container.id}
            container={container}
            activeFilter={activeFilter}
            onAddSlot={handleAddSlot}
            onDeleteRack={handleDeleteRack}
            onDeleteSlot={handleDeleteSlot}
            onExternalSlotClick={(cid, sNum) => {
              // parent gets notified when a slot is clicked
              // we don't need to do anything here for now,
              // but this keeps the connection should you want to show a side panel
              // also preserves original modal behavior inside ContainerGrid
            }}
          />
        ))}
      </div>
    </div>
  );
};

export default Containers;
