import React from "react";
import { Package, LogOut, Menu } from "lucide-react";
import "bootstrap/dist/css/bootstrap.min.css";

export const TopNavbar = ({ admin, onMenuClick, sidebarOpen }) => {
  const getInitials = (name) => {
    return name
      .split(" ")
      .map((n) => n[0])
      .join("")
      .toUpperCase();
  };

  return (
    <header className="border-bottom bg-light py-3 px-3 px-md-4">
      <div className="d-flex justify-content-between align-items-center">
        {/* Left Section */}
        <div className="d-flex align-items-center gap-3">
          {/* Menu toggle (mobile) */}
          <button
            className="btn btn-outline-secondary d-lg-none"
            onClick={onMenuClick}
          >
            <Menu
              className={`transition-transform ${
                sidebarOpen ? "rotate-90" : ""
              }`}
              size={20}
            />
          </button>

          {/* Logo */}
          <Package className="text-primary" size={28} />

          {/* Title */}
          <div>
            <h1 className="h5 fw-bold mb-0">ContainerHub</h1>
            <small className="text-muted d-none d-sm-block">
              Admin Portal
            </small>
          </div>
        </div>

        {/* Right Section */}
        <div className="d-flex align-items-center gap-3">
          {/* Admin Info */}
          <div className="text-end d-none d-md-block">
            <p className="mb-0 fw-semibold">{admin.name}</p>
            <small className="text-muted">{admin.email}</small>
          </div>

          {/* Avatar */}
          <div
            className="rounded-circle border border-primary bg-primary text-white d-flex justify-content-center align-items-center"
            style={{ width: "40px", height: "40px", fontWeight: "bold" }}
          >
            {getInitials(admin.name)}
          </div>

          {/* Logout Button */}
          <button className="btn btn-outline-danger p-2">
            <LogOut size={20} />
          </button>
        </div>
      </div>
    </header>
  );
};
