// components/admin/dashboard/LowStockCard.jsx
import React from "react";

const LowStockCard = ({ containers = [], onJumpToSlot }) => {
  const lowItems = [];

  // build low-stock list
  containers.forEach((rack) => {
    rack.slots.forEach((slot) => {
      (slot.items || []).forEach((item) => {
        const totalTaken = (item.taken || []).reduce(
          (n, t) => n + Number(t.qty || 0),
          0
        );
        const remaining = Number(item.quantity || 0) - totalTaken;

        if (remaining < 6) {
          lowItems.push({
            itemName: item.name,
            remaining,
            rackName: rack.name,
            slotNumber: slot.slotNumber,
            containerId: rack.id,
          });
        }
      });
    });
  });

  // click handler: jump + attempt to click slot add button
  const handleAddClick = (containerId, slotNumber) => {
    // 1) scroll/jump to the slot
    onJumpToSlot?.(containerId, slotNumber);

    // 2) after a short delay try to find the slot's internal "Add" button and click it.
    //    This requires the Add button inside SlotCard to have a stable selector/class
    //    (we'll ask you to add className="slot-add-btn" to that button in SlotCard).
    setTimeout(() => {
      try {
        const slotEl = document.getElementById(
          `rack-${containerId}-slot-${slotNumber}`
        );
        if (!slotEl) return;

        // find the add button inside the slot element
        // NOTE: this uses a class selector; ensure SlotCard's add button has this class.
        const addBtn = slotEl.querySelector(".slot-add-btn");
        if (addBtn) {
          addBtn.click();
        } else {
          // fallback: try any button with a plus icon text inside the slot
          const fallback = slotEl.querySelector("button");
          if (fallback) fallback.click();
        }
      } catch (err) {
        console.warn("Auto-open add button failed:", err);
      }
    }, 300); // 300ms works in most UIs — increase if your page is slow
  };

  return (
    <div
      className="card shadow-sm"
      style={{
        background: "hsl(215, 25%, 12%)",
        border: "1px solid hsl(215, 20%, 25%)",
        color: "white",
        height: "100%",
      }}
    >
      <div className="card-header fw-bold">Limited Stock Items</div>

      <div
        className="card-body"
        style={{
          maxHeight: "320px",
          overflowY: "auto",
          paddingRight: "6px",
        }}
      >
        {lowItems.length === 0 ? (
          <p className="text-muted">No low-stock items</p>
        ) : (
          lowItems.map((it, idx) => (
            <div
              key={`${it.containerId}-${it.slotNumber}-${idx}`}
              className="d-flex align-items-center justify-content-between p-3 mb-3 rounded"
              style={{
                background: "hsl(215, 25%, 16%)",
                border: "1px solid hsl(215, 20%, 25%)",
                gap: "12px",
              }}
            >
              <div style={{ flex: 1 }}>
                <div className="fw-semibold">{it.itemName}</div>

                <div className="text-muted small">
                  Remaining: {it.remaining}
                </div>

                <div className="text-muted small">
                  {it.rackName} • Slot {it.slotNumber}
                </div>
              </div>

              <div style={{ marginLeft: 12 }}>
                <button
                  className="btn btn-primary btn-sm"
                  onClick={() => handleAddClick(it.containerId, it.slotNumber)}
                  title={`Jump to ${it.rackName} • Slot ${it.slotNumber} and open Add Item`}
                >
                  + Add
                </button>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default LowStockCard;
