import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { Clock } from "lucide-react";
import { format } from "date-fns";

const RecentActivity = ({ containers }) => {
  // ✅ Extract and sort recent items
  const recentItems = containers
    .flatMap((container) =>
      container.slots
        .filter((slot) => slot.item && slot.item.takenDate)
        .map((slot) => ({
          ...slot.item,
          containerName: container.name,
          slotNumber: slot.slotNumber,
        }))
    )
    .sort(
      (a, b) =>
        new Date(b.takenDate).getTime() - new Date(a.takenDate).getTime()
    )
    .slice(0, 5);

  return (
    <div className="card shadow-sm border-0 my-3">
      {/* Header */}
      <div className="card-header bg-light d-flex align-items-center gap-2">
        <Clock className="text-primary" size={18} />
        <h5 className="mb-0">Recent Activity</h5>
      </div>

      {/* Content */}
      <div className="card-body">
        {recentItems.length === 0 ? (
          <p className="text-center text-muted py-4 mb-0">
            No recent activity
          </p>
        ) : (
          recentItems.map((item, idx) => (
            <div
              key={idx}
              className="d-flex justify-content-between align-items-center p-3 mb-2 border rounded bg-light-subtle hover-shadow"
              style={{ transition: "0.3s" }}
              onMouseEnter={(e) =>
                (e.currentTarget.style.backgroundColor = "#f8f9fa")
              }
              onMouseLeave={(e) =>
                (e.currentTarget.style.backgroundColor = "")
              }
            >
              {/* Left Info */}
              <div>
                <p className="fw-semibold mb-1 small">{item.name}</p>
                <p className="text-muted small mb-0">
                  {item.containerName} - Slot {item.slotNumber}
                </p>
              </div>

              {/* Right Info */}
              <div className="text-end">
                <span
                  className={`badge ${
                    item.status === "non-returnable"
                      ? "bg-danger"
                      : "bg-secondary"
                  }`}
                >
                  {item.status === "non-returnable"
                    ? "Non-Returnable"
                    : "Borrowed"}
                </span>
                <p className="text-muted small mb-0 mt-1">{item.takenBy}</p>
                <p className="text-muted small mb-0">
                  {format(new Date(item.takenDate), "MMM dd, HH:mm")}
                </p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default RecentActivity;
