import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import { CircleUserRound } from "lucide-react";

const UserProfile = ({ currentUser, onUpdateUser }) => {
  const [editMode, setEditMode] = useState(false);

  const [form, setForm] = useState({
    name: currentUser?.name || "",
    email: currentUser?.email || "",
    phone: currentUser?.phone || "",
    role: currentUser?.role || "User",
  });

  const handleSave = () => {
    if (!form.name || !form.email || !form.phone) {
      alert("Please fill all fields.");
      return;
    }
    onUpdateUser(form);
    setEditMode(false);
  };

  return (
    <div className="container my-4">
      <div
        className="card mx-auto d-flex flex-row align-items-center p-3"
        style={{
          maxWidth: 700,
          height: 140,
          background: "hsl(215, 25%, 12%)",
          color: "white",
          borderRadius: 12,
          border: "1px solid hsl(215, 20%, 25%)",
          boxShadow: "0 6px 25px rgba(0,0,0,0.5)",
          gap: "1.5rem",
        }}
      >
        {/* Avatar and Basic Info Panel */}
        <div
          className="d-flex flex-column align-items-center justify-content-center text-center"
          style={{ width: 130 }}
        >
          <div
            className="rounded-circle d-flex justify-content-center align-items-center mb-2"
            style={{
              width: 72,
              height: 72,
              background: "hsl(215, 25%, 16%)",
              border: "2px solid hsl(215, 20%, 25%)",
            }}
          >
            <CircleUserRound size={40} />
          </div>
          <h5 className="fw-semibold mb-0">{form.name || "Your Name"}</h5>
          <small style={{ color: "hsl(215, 15%, 75%)" }}>{form.role}</small>
        </div>

        {/* Form Fields Panel */}
        <div className="flex-grow-1">
          <Form className="d-flex gap-3 flex-wrap">
            <Form.Group className="flex-grow-1" style={{ minWidth: 130 }}>
              <Form.Label>Name</Form.Label>
              <Form.Control
                disabled={!editMode}
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                style={{
                  backgroundColor: "hsl(215, 25%, 12%)",
                  color: "white",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              />
            </Form.Group>

            <Form.Group className="flex-grow-1" style={{ minWidth: 180 }}>
              <Form.Label>Email</Form.Label>
              <Form.Control
                disabled={!editMode}
                type="email"
                value={form.email}
                onChange={(e) => setForm({ ...form, email: e.target.value })}
                style={{
                  backgroundColor: "hsl(215, 25%, 12%)",
                  color: "white",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              />
            </Form.Group>

            <Form.Group className="flex-grow-1" style={{ minWidth: 160 }}>
              <Form.Label>Phone</Form.Label>
              <Form.Control
                disabled={!editMode}
                value={form.phone}
                onChange={(e) => setForm({ ...form, phone: e.target.value })}
                style={{
                  backgroundColor: "hsl(215, 25%, 12%)",
                  color: "white",
                  border: "1px solid hsl(215, 20%, 25%)",
                }}
              />
            </Form.Group>

            <Form.Group style={{ minWidth: 120 }}>
              <Form.Label>Role</Form.Label>
              <Form.Control
                disabled
                value={form.role}
                style={{
                  backgroundColor: "hsl(215, 25%, 12%)",
                  color: "white",
                  border: "1px solid hsl(215, 20%, 25%)",
                  opacity: 0.7,
                }}
              />
            </Form.Group>
          </Form>
        </div>

        {/* Buttons */}
        <div className="d-flex flex-column gap-2 ms-3">
          {!editMode ? (
            <Button variant="primary" onClick={() => setEditMode(true)} style={{ minWidth: 110 }}>
              Edit
            </Button>
          ) : (
            <>
              <Button
                variant="secondary"
                onClick={() => setEditMode(false)}
                style={{ minWidth: 110 }}
              >
                Cancel
              </Button>
              <Button variant="success" onClick={handleSave} style={{ minWidth: 110 }}>
                Save
              </Button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
